### **Content Caching & Anti-Repetition:**

```javascript
class ContentManager {
  constructor() {
    this.recentTexts = new Set(); // Last 20 texts
    this.maxCacheSize = 20;
  }

  async generateUniqueText(difficulty, userId) {
    let attempts = 0;
    let generatedText = null;

    // Try up to 3 times to get unique content
    while (attempts < 3) {
      const topic = selectRandomTopic(difficulty);
      const prompt = DIFFICULTY_PROMPTS[difficulty].systemPrompt
        .replace('${topic}', topic)
        .replace('${codingLevel}', difficulty)
        .replace('${codeType}', selectRandomTopic('coding'));

      generatedText = await callGeminiAPI(prompt);

      // Check if we've seen this text recently
      const textHash = hashText(generatedText);
      if (!this.recentTexts.has(textHash)) {
        this.addToCache(textHash);
        break;
      }

      attempts++;
    }

    // Validate difficulty meets requirements
    if (!this.validateDifficulty(generatedText, difficulty)) {
      console.warn('Generated text failed validation, regenerating...');
      return this.generateUniqueText(difficulty, userId); // Retry
    }

    return generatedText;
  }

  validateDifficulty(text, difficulty) {
    const rules = DIFFICULTY_PROMPTS[difficulty].validation;
    const stats = analyzeText(text);

    // Check word count
    if (stats.wordCount < rules.totalWords.min || 
        stats.wordCount > rules.totalWords.max) {
      return false;
    }

    // Check average word length
    if (stats.avgWordLength < rules.avgWordLength.min || 
        stats.avgWordLength > rules.avgWordLength.max) {
      return false;
    }

    // Check for required complexity
    if (difficulty === 'hard' && !stats.hasComplexPunctuation) {
      return false;
    }

    return true;
  }

  addToCache(textHash) {
    this.recentTexts.add(textHash);
    if (this.recentTexts.size > this.maxCacheSize) {
      const firstItem = this.recentTexts.values().next().value;
      this.recentTexts.delete(firstItem);
    }
  }
}

function analyzeText(text) {
  const words = text.split(/\s+/);
  const totalLength = words.reduce((sum, word) => sum + word.length, 0);

  return {
    wordCount: words.length,
    avgWordLength: totalLength / words.length,
    hasComplexPunctuation: /[;:()"']/.test(text),
    hasSpecialChars: /[@#$%&*]/.test(text),
    hasNumbers: /\d/.test(text)
  };
}
