### **4. Replace AuthContext with Supabase Authentication**

**Update file:** `contexts/AuthContext.tsx`

**Replace the ENTIRE contents with:**

```typescript
import { createContext, useContext, useEffect, useState, ReactNode } from 'react';
import { supabase, User } from '@/services/supabase';
import { Session } from '@supabase/supabase-js';

interface AuthContextType {
  user: User | null;
  session: Session | null;
  loading: boolean;
  signUp: (email: string, password: string, username: string) => Promise<void>;
  signIn: (email: string, password: string) => Promise<void>;
  signOut: () => Promise<void>;
  updateUserStats: (stats: Partial<User>) => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [loading, setLoading] = useState(true);

  // Initialize auth session on mount
  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
      if (session?.user) {
        loadUserProfile(session.user.id);
      } else {
        setLoading(false);
      }
    });

    // Listen for auth state changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      async (event, session) => {
        console.log('Auth state changed:', event);
        setSession(session);
        if (session?.user) {
          await loadUserProfile(session.user.id);
        } else {
          setUser(null);
        }
        setLoading(false);
      }
    );

    return () => subscription.unsubscribe();
  }, []);

  // Load user profile from database
  async function loadUserProfile(userId: string) {
    try {
      const { data, error } = await supabase
        .from('users')
        .select('*')
        .eq('id', userId)
        .single();

      if (error) {
        console.error('Error loading profile:', error);
        throw error;
      }

      setUser(data);
    } catch (error) {
      console.error('Failed to load user profile:', error);
    } finally {
      setLoading(false);
    }
  }

  // Sign up new user
  async function signUp(email: string, password: string, username: string) {
    try {
      // 1. Create authentication user
      const { data: authData, error: authError } = await supabase.auth.signUp({
        email,
        password,
      });

      if (authError) throw authError;
      if (!authData.user) throw new Error('User creation failed');

      // 2. Create user profile in database
      const { error: profileError } = await supabase
        .from('users')
        .insert({
          id: authData.user.id,
          username: username,
          xp: 0,
          rank: 'Bronze I',
          level: 1,
          best_wpm: 0,
          average_wpm: 0,
          total_matches: 0,
          wins: 0,
          losses: 0,
          win_streak: 0,
          character_stats: {}
        });

      if (profileError) throw profileError;

      // 3. Load the newly created profile
      await loadUserProfile(authData.user.id);

      console.log('Signup successful for:', username);

    } catch (error: any) {
      console.error('Signup error:', error);
      throw new Error(error.message || 'Signup failed. Please try again.');
    }
  }

  // Sign in existing user
  async function signIn(email: string, password: string) {
    try {
      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (error) throw error;

      // Update last login timestamp
      if (data.user) {
        await supabase
          .from('users')
          .update({ last_login: new Date().toISOString() })
          .eq('id', data.user.id);
      }

      console.log('Login successful');

    } catch (error: any) {
      console.error('Login error:', error);
      throw new Error(error.message || 'Invalid email or password');
    }
  }

  // Sign out user
  async function signOut() {
    try {
      const { error } = await supabase.auth.signOut();
      if (error) throw error;

      setUser(null);
      setSession(null);
      console.log('Logout successful');

    } catch (error) {
      console.error('Logout error:', error);
      throw error;
    }
  }

  // Update user stats (after matches, level ups, etc.)
  async function updateUserStats(stats: Partial<User>) {
    if (!user) {
      console.warn('Cannot update stats: No user logged in');
      return;
    }

    try {
      const { error } = await supabase
        .from('users')
        .update(stats)
        .eq('id', user.id);

      if (error) throw error;

      // Update local state
      setUser({ ...user, ...stats });

      console.log('User stats updated:', stats);

    } catch (error) {
      console.error('Error updating user stats:', error);
      throw error;
    }
  }

  const value = {
    user,
    session,
    loading,
    signUp,
    signIn,
    signOut,
    updateUserStats
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}

// Custom hook to use auth context
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
};
```

