### **5. Create Match History Service**

**Create file:** `services/matchService.ts`

```typescript
import { supabase } from './supabase';

// Save match result to database
export async function saveMatchHistory(
  userId: string,
  matchData: {
    mode: 'practice' | 'multiplayer';
    difficulty: 'easy' | 'medium' | 'hard' | 'coding';
    wpm: number;
    accuracy: number;
    xp_earned: number;
    result?: 'win' | 'loss';
    text_used?: string;
    time_taken?: number;
    mistakes?: number;
  }
) {
  try {
    const { data, error } = await supabase
      .from('match_history')
      .insert({
        user_id: userId,
        ...matchData,
        created_at: new Date().toISOString()
      })
      .select()
      .single();

    if (error) throw error;

    console.log('Match saved:', data);
    return data;

  } catch (error) {
    console.error('Error saving match history:', error);
    throw error;
  }
}

// Get user's match history
export async function getMatchHistory(userId: string, limit = 50) {
  try {
    const { data, error } = await supabase
      .from('match_history')
      .select('*')
      .eq('user_id', userId)
      .order('created_at', { ascending: false })
      .limit(limit);

    if (error) throw error;

    return data || [];

  } catch (error) {
    console.error('Error fetching match history:', error);
    return [];
  }
}

// Get recent matches for analytics
export async function getRecentMatches(userId: string, days = 7) {
  const dateFrom = new Date();
  dateFrom.setDate(dateFrom.getDate() - days);

  try {
    const { data, error } = await supabase
      .from('match_history')
      .select('*')
      .eq('user_id', userId)
      .gte('created_at', dateFrom.toISOString())
      .order('created_at', { ascending: true });

    if (error) throw error;

    return data || [];

  } catch (error) {
    console.error('Error fetching recent matches:', error);
    return [];
  }
}
```

