        .from('users')
        .select('*')
        .eq('id', userId)
        .single();

      if (error || !data) {
          console.error("Profile not found or error:", error);
          setLoading(false);
          return;
      }

      // Fetch match history separately to populate user state
      const matches = await getMatchHistory(userId);

      const dbUser = data as DbUser;
      setUser({
        id: dbUser.id,
        username: dbUser.username,
        isGuest: false,
        xp: dbUser.xp,
        rank: dbUser.rank as Rank,
        bestWpm: dbUser.best_wpm,
        avgWpm: dbUser.avg_wpm,
        winStreak: dbUser.win_streak,
        matches: matches
      });
    } catch (err) {
      console.error('Failed to load profile:', err);
    } finally {
      setLoading(false);
    }
  }

  const login = async (email: string, pass: string) => {
    const { error } = await supabase.auth.signInWithPassword({ email, password: pass });
    if (error) throw error;
  };

  const signup = async (email: string, pass: string, username: string) => {
    const { data, error } = await supabase.auth.signUp({ email, password: pass });
    if (error) throw error;
    if (!data.user) throw new Error("Signup failed");

    const { error: profileError } = await supabase.from('users').insert({
      id: data.user.id,
      username: username,
      xp: 0,
      rank: Rank.BRONZE_I,
      best_wpm: 0,
      avg_wpm: 0,
      win_streak: 0
    });

    if (profileError) throw profileError;
  };

  const guestLogin = () => {
    localStorage.setItem('typearena_is_guest', 'true');
    setUser({
      id: 'guest_' + Date.now(),
      username: 'Guest',
      isGuest: true,
      xp: 0,
      rank: Rank.BRONZE_I,
      matches: [],
      bestWpm: 0,
      avgWpm: 0,
      winStreak: 0
    });
    setLoading(false);
  };

  const logout = async () => {
    await supabase.auth.signOut();
    localStorage.removeItem('typearena_is_guest');
    setUser(null);
  };

  const addMatch = async (stats: MatchStats, baseXp: number, isMultiplayerWin?: boolean) => {
    if (!user) return;

    let newStreak = user.winStreak || 0;
    let finalXp = baseXp;

    if (stats.mode === GameMode.MULTIPLAYER && typeof isMultiplayerWin !== 'undefined') {
        if (isMultiplayerWin) {
            newStreak += 1;
            if (newStreak > 1) {
                const streakBonus = Math.min(newStreak * 10, 100);
                finalXp += streakBonus;
            }
        } else {
            newStreak = 0;
        }
    }

    const currentRank = user.rank;
    const newXp = user.xp + finalXp;
    const newRank = getRankFromXP(newXp);

    if (newRank !== currentRank) {
        setLevelUpEvent({ oldRank: currentRank, newRank: newRank });
    }

    const newMatches = [stats, ...user.matches].slice(0, 50);
    const newBest = Math.max(user.bestWpm, stats.wpm);
    const totalWpm = newMatches.reduce((acc, m) => acc + m.wpm, 0);
    const avg = newMatches.length > 0 ? Math.round(totalWpm / newMatches.length) : 0;

    const updatedUser: User = {
      ...user,
      xp: newXp,
      rank: newRank,
      matches: newMatches,
      bestWpm: newBest,
      avgWpm: avg,
      winStreak: newStreak
    };

    setUser(updatedUser);

    if (!user.isGuest) {
        // Sync to Supabase
        try {
            await saveMatchHistory(user.id, stats);
            await supabase.from('users').update({
                xp: newXp,
                rank: newRank,
                best_wpm: newBest,
                avg_wpm: avg,
                win_streak: newStreak
            }).eq('id', user.id);
        } catch (err) {
            console.error("Supabase Sync Error:", err);
        }
    }
  };

  const dismissLevelUp = () => {
      setLevelUpEvent(null);
  };

  return (
    <AuthContext.Provider value={{ user, loading, levelUpEvent, dismissLevelUp, login, signup, guestLogin, logout, addMatch }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

