      winStreak: newStreak
    };

    setUser(updatedUser);

    if (!user.isGuest) {
        try {
            await saveMatchHistory(user.id, stats);
            // Column names updated to match public.users table (average_wpm)
            await supabase.from('users').update({
                xp: newXp,
