import { GameMode, GameResult, Difficulty, Opponent, Rank } from '../types';
import { generatePracticeText } from '../services/geminiService';
import TypingEngine from '../components/TypingEngine';
import { useAuth } from '../contexts/AuthContext';
import { supabase } from '../services/supabase';
import { Loader2, Zap, Check, Copy, Lock, Globe, Users, User as UserIcon } from 'lucide-react';
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';

const BOT_NAMES = ['Speedster_99', 'CodeNinja', 'TypeMaster_X', 'KeyboardWarrior', 'FingerSlippage', 'Glitch_Runner'];
const COLORS = ['#ef4444', '#f59e0b', '#10b981', '#8b5cf6', '#ec4899', '#06b6d4'];

interface LeaderboardEntry {
  id: string;
  rank: number;
  name: string;
  wpm: number;
  accuracy: number;
  isUser: boolean;
}

type LobbyMode = 'SELECT' | 'HOSTING' | 'JOINED' | 'SEARCHING';

const Multiplayer: React.FC = () => {
  const { addMatch, user } = useAuth();
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();

  const [lobbyMode, setLobbyMode] = useState<LobbyMode>('SELECT');
  const [status, setStatus] = useState<'IDLE' | 'SEARCHING' | 'COUNTDOWN' | 'RACING' | 'FINISHED'>('IDLE');
  const [text, setText] = useState('');
  const [opponents, setOpponents] = useState<Opponent[]>([]);
  const [countdown, setCountdown] = useState(3);
  const [result, setResult] = useState<GameResult | null>(null);
  const [playerWon, setPlayerWon] = useState(false);
  const [placement, setPlacement] = useState(0);
  const [loadingText, setLoadingText] = useState("Finding opponents...");
  const [leaderboard, setLeaderboard] = useState<LeaderboardEntry[]>([]);

  const [lobbyId, setLobbyId] = useState<string>('');
  const [hostName, setHostName] = useState<string>('');
  const [hostWpm, setHostWpm] = useState<number>(0);
  const [copied, setCopied] = useState(false);
  const [isPrivateMatch, setIsPrivateMatch] = useState(false);
  const [peerCount, setPeerCount] = useState(0);

  const raceIntervalRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const channelRef = useRef<any>(null);

  const getDifficultyByRank = (rank: Rank | undefined): Difficulty => {
    if (!rank) return Difficulty.EASY;
    const r = rank.toString();
    if (r.includes('Bronze') || r.includes('Silver')) return Difficulty.EASY;
    if (r.includes('Gold') || r.includes('Platinum')) return Difficulty.MEDIUM;
    return Difficulty.HARD;
  };

  // Realtime Channel Management
  useEffect(() => {
    if (!lobbyId || !user) return;

    const channel = supabase.channel(`lobby:${lobbyId}`, {
      config: {
        presence: { key: user.id },
      },
    });

    channel
      .on('presence', { event: 'sync' }, () => {
        const state = channel.presenceState();
        setPeerCount(Object.keys(state).length);
      })
      .on('broadcast', { event: 'START_RACE' }, (payload) => {
        if (lobbyMode === 'JOINED') {
            setText(payload.payload.text);
            setupOpponent(payload.payload.hostData);
            setStatus('COUNTDOWN');
        }
      })
      .subscribe(async (status) => {
        if (status === 'SUBSCRIBED') {
          await channel.track({
            id: user.id,
            username: user.username,
            avgWpm: user.avgWpm || 40,
            online_at: new Date().toISOString(),
          });
        }
      });

    channelRef.current = channel;

    return () => {
      channel.unsubscribe();
      channelRef.current = null;
    };
  }, [lobbyId, user?.id, lobbyMode]);

  useEffect(() => {
    const lobbyParam = searchParams.get('lobby');
    const hostParam = searchParams.get('host');
    const wpmParam = searchParams.get('wpm');
    const modeParam = searchParams.get('mode');

    if (lobbyParam) {
        setLobbyId(lobbyParam);
        setIsPrivateMatch(true);
        if (hostParam) {
            setLobbyMode('JOINED'); 
            setHostName(decodeURIComponent(hostParam));
            setHostWpm(wpmParam ? parseInt(wpmParam) : 40);
        } else {
            setLobbyMode('HOSTING'); 
        }
    } else if (modeParam === 'host') {
        handleCreateLobby();
    }
    return () => {
        if (raceIntervalRef.current) clearInterval(raceIntervalRef.current);
    };
  }, [searchParams]);

  const handleCreateLobby = () => {
      const newId = Math.random().toString(36).substring(7).toUpperCase();
      setLobbyId(newId);
      setIsPrivateMatch(true);
      setLobbyMode('HOSTING');
  };

  const handleStartRanked = () => {
      setIsPrivateMatch(false);
      setLobbyMode('SEARCHING');
      setLobbyId('GLOBAL_' + Math.random().toString(36).substring(7).toUpperCase());
      startMatchmaking(false);
  };

  const setupOpponent = (opponentData: { name: string, wpm: number }) => {
      const newOpponent: Opponent = {
          id: 'opponent-1',
          name: opponentData.name,
          progress: 0,
          wpm: opponentData.wpm,
          accuracy: 96,
          color: COLORS[0],
          isFinished: false
      };
      setOpponents([newOpponent]);
  };

  const startPrivateMatch = async () => {
    if (peerCount < 2) return; // Guard clause for host

    setStatus('SEARCHING');
    setLoadingText("Synchronizing players...");

    const difficulty = getDifficultyByRank(user?.rank);
    const generatedText = await generatePracticeText(difficulty);
    setText(generatedText);

    // Broadcast to joined peer
    if (channelRef.current) {
        channelRef.current.send({
            type: 'broadcast',
            event: 'START_RACE',
            payload: { 
                text: generatedText,
                hostData: { name: user?.username, wpm: user?.avgWpm || 40 }
            }
        });
    }

    // Set up host's view of the opponent
    // We try to find the other user's info from presence
    const presence = channelRef.current.presenceState();
    const otherUserId = Object.keys(presence).find(id => id !== user?.id);
    const otherUser = otherUserId ? presence[otherUserId][0] : null;

    setupOpponent({ 
        name: otherUser?.username || hostName || "Challenger", 
        wpm: otherUser?.avgWpm || hostWpm || 40 
    });

    setStatus('COUNTDOWN');
  };

  const startMatchmaking = async (isPrivate: boolean) => {
    setStatus('SEARCHING');
    setResult(null);
    setPlayerWon(false);
    setPlacement(0);
    setLeaderboard([]);

    setLoadingText(isPrivate ? "Connecting..." : "Searching...");

    const difficulty = getDifficultyByRank(user?.rank);
    const generatedText = await generatePracticeText(difficulty);
    setText(generatedText);

    const newOpponents: Opponent[] = [];
    const r = user?.rank || Rank.BRONZE_I;
    let minWpm = 15;
    let maxVar = 15;

    if (r.includes('Bronze')) { minWpm = 15; maxVar = 15; }
    else if (r.includes('Silver')) { minWpm = 30; maxVar = 20; }
    else if (r.includes('Gold')) { minWpm = 45; maxVar = 20; }
    else { minWpm = 70; maxVar = 30; }

    const numBots = Math.floor(Math.random() * 2) + 1; 

    for (let i = 0; i < numBots; i++) {
        newOpponents.push({
            id: `bot-${i}`,
            name: BOT_NAMES[Math.floor(Math.random() * BOT_NAMES.length)],
            progress: 0,
            wpm: Math.max(12, Math.floor(Math.random() * maxVar) + minWpm),
            accuracy: 94,
            color: COLORS[i % COLORS.length],
            isFinished: false
        });
    }
    setOpponents(newOpponents);
    setStatus('COUNTDOWN');
  };

  useEffect(() => {
    if (status === 'COUNTDOWN') {
        if (countdown > 0) {
            const timer = setTimeout(() => setCountdown(c => c - 1), 1000);
            return () => clearTimeout(timer);
        } else {
            setStatus('RACING');
            startBotRace();
        }
    } else if (status === 'SEARCHING') {
        setCountdown(3);
    }
  }, [status, countdown]);

  const startBotRace = () => {
      const startTime = Date.now();
      const textLength = text.length;
      raceIntervalRef.current = setInterval(() => {
          setOpponents(prev => {
              const now = Date.now();
              const elapsedMin = (now - startTime) / 60000;
              return prev.map(bot => {
                  if (bot.isFinished) return bot;
                  const expectedChars = bot.wpm * 5 * elapsedMin;
                  const progress = Math.min(100, (expectedChars / textLength) * 100);
                  return { ...bot, progress, isFinished: progress >= 100 };
              });
          });
      }, 50);
  };

  const handleFinish = (res: GameResult) => {
      if (raceIntervalRef.current) clearInterval(raceIntervalRef.current);
      const allParticipants = [
          { id: 'user', name: user?.username || 'You', wpm: res.wpm, accuracy: res.accuracy, isUser: true },
          ...opponents.map(bot => ({ id: bot.id, name: bot.name, wpm: bot.wpm, accuracy: bot.accuracy, isUser: false }))
      ];
      allParticipants.sort((a, b) => b.wpm - a.wpm);
      const rankedResults: LeaderboardEntry[] = allParticipants.map((p, index) => ({ ...p, rank: index + 1 }));
      setLeaderboard(rankedResults);

      const userRank = rankedResults.find(p => p.isUser)?.rank || rankedResults.length;
      const isWin = userRank === 1;

      setPlacement(userRank);
      setPlayerWon(isWin);
      setStatus('FINISHED');
      setResult(res);

      addMatch({
        id: Date.now().toString(),
        date: new Date().toISOString(),
        wpm: res.wpm,
        accuracy: res.accuracy,
        mode: GameMode.MULTIPLAYER,
        difficulty: getDifficultyByRank(user?.rank),
        errors: res.errors
      }, isWin ? 200 : 100, isWin); 
  };

  const copyInviteLink = () => {
      const baseUrl = window.location.origin + window.location.pathname;
      const userName = encodeURIComponent(user?.username || 'Player');
      const wpm = user?.avgWpm || 40;
      const url = `${baseUrl}#/multiplayer?lobby=${lobbyId}&host=${userName}&wpm=${wpm}`;
      navigator.clipboard.writeText(url).then(() => {
          setCopied(true);
          setTimeout(() => setCopied(false), 2000);
      });
  };

  if (lobbyMode === 'SELECT' && status === 'IDLE') {
      return (
          <div className="max-w-4xl mx-auto py-8">
              <h2 className="text-3xl font-bold text-center text-slate-800 dark:text-white mb-8 italic">READY FOR WAR?</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div onClick={handleStartRanked} className="group p-8 bg-white dark:bg-abyss rounded-xl border border-slate-200 dark:border-white/5 hover:border-neon-cyan cursor-pointer shadow-sm hover:shadow-xl hover:shadow-neon-cyan/10 transform transition-all duration-300 hover:-translate-y-2 text-center">
                      <Globe size={40} className="mx-auto mb-4 text-neon-cyan" />
                      <h3 className="text-xl font-bold text-slate-800 dark:text-white">Ranked Arena</h3>
                      <p className="text-slate-500 mb-4">Fastest match-up against bots & players.</p>
                      <span className="text-neon-cyan font-bold text-sm uppercase">Quick Start</span>
                  </div>

                  <div onClick={handleCreateLobby} className="group p-8 bg-white dark:bg-abyss rounded-xl border border-slate-200 dark:border-white/5 hover:border-neon-purple cursor-pointer shadow-sm hover:shadow-xl hover:shadow-neon-purple/10 transform transition-all duration-300 hover:-translate-y-2 text-center">
                      <Lock size={40} className="mx-auto mb-4 text-neon-purple" />
                      <h3 className="text-xl font-bold text-slate-800 dark:text-white">Private Duel</h3>
                      <p className="text-slate-500 mb-4">Requires a real opponent to join link.</p>
                      <span className="text-neon-purple font-bold text-sm uppercase">Create Room</span>
                  </div>
              </div>
          </div>
      );
  }

  if (lobbyMode === 'JOINED' && status === 'IDLE') {
    return (
        <div className="max-w-md mx-auto py-12 text-center space-y-6">
            <div className="p-6 bg-neon-cyan/10 border border-neon-cyan/20 rounded-2xl">
                <Users size={32} className="mx-auto text-neon-cyan mb-3" />
                <h2 className="text-2xl font-bold text-slate-800 dark:text-white">Lobby Joined</h2>
                <p className="text-slate-500">Challenging: <span className="text-neon-cyan font-bold">{hostName}</span></p>
            </div>
            <div className="flex flex-col items-center gap-2">
                <Loader2 className="animate-spin text-neon-cyan" size={24} />
                <p className="text-sm font-mono text-slate-400 uppercase tracking-widest">Waiting for host to start...</p>
            </div>
            <button onClick={() => { setLobbyMode('SELECT'); setIsPrivateMatch(false); }} className="text-slate-500 hover:text-red-500 text-sm font-bold">CANCEL MISSION</button>
        </div>
    );
  }

  if (lobbyMode === 'HOSTING' && status === 'IDLE') {
      return (
          <div className="max-w-lg mx-auto py-12 text-center">
              <h2 className="text-2xl font-bold text-slate-800 dark:text-white mb-6">Awaiting Challenger</h2>

              <div className="bg-slate-100 dark:bg-white/5 p-4 rounded-xl flex items-center gap-3 mb-8 border border-slate-200 dark:border-white/10">
                  <div className="flex-1 text-xs text-slate-500 truncate text-left font-mono">Invite your opponent...</div>
                  <button onClick={copyInviteLink} className="flex items-center gap-2 px-4 py-2 bg-neon-purple text-white rounded-lg font-bold hover:scale-105 active:scale-95 transition-transform text-xs">
                      {copied ? <Check size={14} /> : <Copy size={14} />}
                      {copied ? 'COPIED' : 'COPY LINK'}
                  </button>
              </div>

              <div className={`p-6 rounded-2xl border-2 mb-8 transition-colors ${peerCount >= 2 ? 'border-neon-green/30 bg-neon-green/5' : 'border-slate-200 dark:border-white/5 bg-slate-50 dark:bg-white/5'}`}>
                  <div className="flex justify-center -space-x-4 mb-4">
                      <div className="w-12 h-12 rounded-full bg-neon-purple border-4 border-white dark:border-abyss flex items-center justify-center text-white"><UserIcon size={20}/></div>
                      <div className={`w-12 h-12 rounded-full border-4 border-white dark:border-abyss flex items-center justify-center transition-colors ${peerCount >= 2 ? 'bg-neon-cyan text-white' : 'bg-slate-200 dark:bg-slate-800 text-slate-400'}`}>
                        {peerCount >= 2 ? <UserIcon size={20}/> : <span className="text-xs">?</span>}
                      </div>
                  </div>
                  <p className={`text-sm font-bold uppercase tracking-widest ${peerCount >= 2 ? 'text-neon-green' : 'text-slate-400'}`}>
                      {peerCount >= 2 ? 'Opponent Connected' : 'Waiting for connection...'}
                  </p>
              </div>

              <button 
                disabled={peerCount < 2}
                onClick={startPrivateMatch}
                className={`w-full py-4 font-black rounded-xl mb-4 transition-all shadow-xl ${
                    peerCount >= 2 
                    ? 'bg-neon-green text-white hover:scale-[1.02] shadow-neon-green/20' 
                    : 'bg-slate-200 dark:bg-white/5 text-slate-400 cursor-not-allowed'
                }`}
              >
                  {peerCount >= 2 ? 'COMMENCE DUEL' : 'LOCKED (NO OPPONENT)'}
              </button>
              <button onClick={() => { setLobbyMode('SELECT'); setIsPrivateMatch(false); }} className="text-slate-500 hover:text-red-500 text-sm font-bold">ABORT SESSION</button>
          </div>
      );
  }

  if (status === 'SEARCHING') {
      return (
          <div className="flex flex-col items-center justify-center h-64">
              <Loader2 className="animate-spin text-neon-cyan mb-4" size={40} />
              <h2 className="text-xl font-bold text-slate-800 dark:text-white uppercase tracking-widest">{loadingText}</h2>
          </div>
      );
  }

  return (
    <div className="max-w-4xl mx-auto">
        <div className="mb-6 flex justify-between items-end border-b border-slate-200 dark:border-white/5 pb-4">
            <h2 className="text-xl font-bold text-slate-800 dark:text-white flex items-center gap-2">
                <Zap className="text-neon-cyan" size={20} /> Arena Duel
            </h2>
            {status === 'COUNTDOWN' && (
                <div className="text-4xl font-black text-neon-cyan animate-bounce italic">
                    {countdown > 0 ? countdown : 'GO!'}
                </div>
            )}
        </div>

        <TypingEngine 
            text={text}
            isGameActive={status === 'RACING'}
            onGameFinish={handleFinish}
            opponents={opponents}
        />

        {status === 'FINISHED' && result && (
            <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/80 backdrop-blur-sm p-4">
                <div className="bg-white dark:bg-abyss p-8 rounded-3xl shadow-2xl max-w-lg w-full border border-slate-200 dark:border-white/10 animate-pop-in">
                    <h2 className={`text-5xl font-black text-center mb-8 italic tracking-tighter ${playerWon ? 'text-neon-cyan' : 'text-red-500'}`}>
                        {playerWon ? 'VICTORY' : 'DEFEATED'}
                    </h2>

                    <div className="space-y-3 mb-8">
                        {leaderboard.map((entry) => (
                            <div key={entry.id} className={`flex justify-between items-center p-4 rounded-xl border-2 ${entry.isUser ? 'border-neon-cyan/50 bg-neon-cyan/5' : 'border-transparent bg-slate-50 dark:bg-white/5'}`}>
                                <div className="flex gap-4 items-center">
                                    <span className={`text-2xl font-black italic ${entry.rank === 1 ? 'text-yellow-400' : 'text-slate-400'}`}>#{entry.rank}</span>
                                    <span className={`font-bold ${entry.isUser ? 'text-neon-cyan' : 'text-slate-800 dark:text-white'}`}>{entry.name}</span>
                                </div>
                                <span className="font-mono font-black text-xl">{entry.wpm} <span className="text-[10px] text-slate-500">WPM</span></span>
                            </div>
                        ))}
                    </div>

                    <div className="flex gap-4">
                        <button onClick={() => window.location.reload()} className="flex-1 py-4 bg-neon-cyan text-black font-black rounded-xl hover:scale-105 active:scale-95 transition-transform shadow-lg shadow-neon-cyan/20 uppercase tracking-widest">Rematch</button>
                        <button onClick={() => navigate('/')} className="flex-1 py-4 bg-slate-100 dark:bg-white/10 text-slate-500 font-black rounded-xl hover:bg-slate-200 transition-colors uppercase tracking-widest">Exit</button>
                    </div>
                </div>
            </div>
        )}
    </div>
  );
};

export default Multiplayer;

import { GameMode, GameResult, Difficulty, Opponent, Rank } from '../types';
import { generatePracticeText } from '../services/geminiService';
import TypingEngine from '../components/TypingEngine';
import { useAuth } from '../contexts/AuthContext';
import { supabase } from '../services/supabase';
import { Loader2, Zap, Check, Copy, Lock, Globe, Users, User as UserIcon, Timer } from 'lucide-react';
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';

const BOT_NAMES = ['Speedster_99', 'CodeNinja', 'TypeMaster_X', 'KeyboardWarrior', 'FingerSlippage', 'Glitch_Runner'];
const COLORS = ['#ef4444', '#f59e0b', '#10b981', '#8b5cf6', '#ec4899', '#06b6d4'];

interface LeaderboardEntry {
  id: string;
  rank: number;
  name: string;
  wpm: number;
  accuracy: number;
  isUser: boolean;
}

type LobbyMode = 'SELECT' | 'HOSTING' | 'JOINED' | 'SEARCHING';

const Multiplayer: React.FC = () => {
  const { addMatch, user } = useAuth();
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();

  const [lobbyMode, setLobbyMode] = useState<LobbyMode>('SELECT');
  const [status, setStatus] = useState<'IDLE' | 'SEARCHING' | 'COUNTDOWN' | 'RACING' | 'FINISHED'>('IDLE');
  const [text, setText] = useState('');
  const [opponents, setOpponents] = useState<Opponent[]>([]);
  const [countdown, setCountdown] = useState(3);
  const [raceTimer, setRaceTimer] = useState(60);
  const [result, setResult] = useState<GameResult | null>(null);
  const [playerWon, setPlayerWon] = useState(false);
  const [leaderboard, setLeaderboard] = useState<LeaderboardEntry[]>([]);

  const [lobbyId, setLobbyId] = useState<string>('');
  const [hostName, setHostName] = useState<string>('');
  const [hostWpm, setHostWpm] = useState<number>(0);
  const [copied, setCopied] = useState(false);
  const [isPrivateMatch, setIsPrivateMatch] = useState(false);
  const [peerCount, setPeerCount] = useState(0);

  const raceIntervalRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const clockIntervalRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const channelRef = useRef<any>(null);
  const currentStatsRef = useRef<GameResult | null>(null);

  const getDifficultyByRank = (rank: Rank | undefined): Difficulty => {
    if (!rank) return Difficulty.EASY;
    const r = rank.toString();
    if (r.includes('Bronze') || r.includes('Silver')) return Difficulty.EASY;
    if (r.includes('Gold') || r.includes('Platinum')) return Difficulty.MEDIUM;
    return Difficulty.HARD;
  };

  // Realtime Channel Management
  useEffect(() => {
    if (!lobbyId || !user) return;

    const channel = supabase.channel(`lobby:${lobbyId}`, {
      config: {
        presence: { key: user.id },
      },
    });

    channel
      .on('presence', { event: 'sync' }, () => {
        const state = channel.presenceState();
        setPeerCount(Object.keys(state).length);
      })
      .on('broadcast', { event: 'START_RACE' }, (payload) => {
        if (lobbyMode === 'JOINED') {
            setText(payload.payload.text);
            setupOpponent(payload.payload.hostData);
            setStatus('COUNTDOWN');
        }
      })
      .subscribe(async (status) => {
        if (status === 'SUBSCRIBED') {
          await channel.track({
            id: user.id,
            username: user.username,
            avgWpm: user.avgWpm || 40,
            online_at: new Date().toISOString(),
          });
        }
      });

    channelRef.current = channel;

    return () => {
      channel.unsubscribe();
      channelRef.current = null;
    };
  }, [lobbyId, user?.id, lobbyMode]);

  useEffect(() => {
    const lobbyParam = searchParams.get('lobby');
    const hostParam = searchParams.get('host');
    const wpmParam = searchParams.get('wpm');
    const modeParam = searchParams.get('mode');

    if (lobbyParam) {
        setLobbyId(lobbyParam);
        setIsPrivateMatch(true);
        if (hostParam) {
            setLobbyMode('JOINED'); 
            setHostName(decodeURIComponent(hostParam));
            setHostWpm(wpmParam ? parseInt(wpmParam) : 40);
        } else {
            setLobbyMode('HOSTING'); 
        }
    } else if (modeParam === 'host') {
        handleCreateLobby();
    }
    return () => {
        if (raceIntervalRef.current) clearInterval(raceIntervalRef.current);
        if (clockIntervalRef.current) clearInterval(clockIntervalRef.current);
    };
  }, [searchParams]);

  const handleCreateLobby = () => {
      const newId = Math.random().toString(36).substring(7).toUpperCase();
      setLobbyId(newId);
      setIsPrivateMatch(true);
      setLobbyMode('HOSTING');
  };

  const handleStartRanked = () => {
      setIsPrivateMatch(false);
      setLobbyMode('SEARCHING');
      setLobbyId('GLOBAL_' + Math.random().toString(36).substring(7).toUpperCase());
      startMatchmaking(false);
  };

  const setupOpponent = (opponentData: { name: string, wpm: number }) => {
      const newOpponent: Opponent = {
          id: 'opponent-1',
          name: opponentData.name,
          progress: 0,
          wpm: opponentData.wpm,
          accuracy: 96,
          color: COLORS[0],
          isFinished: false
      };
      setOpponents([newOpponent]);
  };

  const startPrivateMatch = async () => {
    if (peerCount < 2) return; 

    setStatus('SEARCHING');
    setLoadingText("Generating Arena Text...");

    // Request a much longer text for marathon mode
    const difficulty = getDifficultyByRank(user?.rank);
    const generatedText = await generatePracticeText(difficulty);
    // Duplicate text to ensure it's long enough for 1 minute
    const marathonText = (generatedText + " ").repeat(5); 
    setText(marathonText);

    if (channelRef.current) {
        channelRef.current.send({
            type: 'broadcast',
            event: 'START_RACE',
            payload: { 
                text: marathonText,
                hostData: { name: user?.username, wpm: user?.avgWpm || 40 }
            }
        });
    }

    const presence = channelRef.current.presenceState();
    const otherUserId = Object.keys(presence).find(id => id !== user?.id);
    const otherUser = otherUserId ? presence[otherUserId][0] : null;

    setupOpponent({ 
        name: otherUser?.username || hostName || "Challenger", 
        wpm: otherUser?.avgWpm || hostWpm || 40 
    });

    setStatus('COUNTDOWN');
  };

  const startMatchmaking = async (isPrivate: boolean) => {
    setStatus('SEARCHING');
    setResult(null);
    setPlayerWon(false);
    setLeaderboard([]);
    setRaceTimer(60);

    setLoadingText("Initializing Server...");

    const difficulty = getDifficultyByRank(user?.rank);
    const generatedText = await generatePracticeText(difficulty);
    const marathonText = (generatedText + " ").repeat(5);
    setText(marathonText);

    const newOpponents: Opponent[] = [];
    const r = user?.rank || Rank.BRONZE_I;
    let minWpm = 20;
    let maxVar = 15;

    if (r.includes('Bronze')) { minWpm = 25; maxVar = 15; }
    else if (r.includes('Silver')) { minWpm = 40; maxVar = 20; }
    else if (r.includes('Gold')) { minWpm = 55; maxVar = 20; }
    else { minWpm = 80; maxVar = 30; }

    const numBots = Math.floor(Math.random() * 2) + 1; 

    for (let i = 0; i < numBots; i++) {
        newOpponents.push({
            id: `bot-${i}`,
            name: BOT_NAMES[Math.floor(Math.random() * BOT_NAMES.length)],
            progress: 0,
            wpm: Math.max(15, Math.floor(Math.random() * maxVar) + minWpm),
            accuracy: 94,
            color: COLORS[i % COLORS.length],
            isFinished: false
        });
    }
    setOpponents(newOpponents);
    setStatus('COUNTDOWN');
  };

  useEffect(() => {
    if (status === 'COUNTDOWN') {
        if (countdown > 0) {
            const timer = setTimeout(() => setCountdown(c => c - 1), 1000);
            return () => clearTimeout(timer);
        } else {
            setStatus('RACING');
            setRaceTimer(60);
            startBotRace();
            startRaceClock();
        }
    }
  }, [status, countdown]);

  const startRaceClock = () => {
      clockIntervalRef.current = setInterval(() => {
          setRaceTimer(prev => {
              if (prev <= 1) {
                  clearInterval(clockIntervalRef.current!);
                  handleRaceTimeout();
                  return 0;
              }
              return prev - 1;
          });
      }, 1000);
  };

  const handleRaceTimeout = () => {
      if (currentStatsRef.current) {
          handleFinish(currentStatsRef.current);
      } else {
          // Fallback if no stats were recorded yet
          handleFinish({ wpm: 0, accuracy: 100, errors: 0, timeTaken: 60, characterStats: {} });
      }
  };

  const startBotRace = () => {
      const startTime = Date.now();
      const textLength = text.length;
      raceIntervalRef.current = setInterval(() => {
          setOpponents(prev => {
              const now = Date.now();
              const elapsedMin = (now - startTime) / 60000;
              return prev.map(bot => {
                  const expectedChars = bot.wpm * 5 * elapsedMin;
                  const progress = Math.min(100, (expectedChars / textLength) * 100);
                  return { ...bot, progress };
              });
          });
      }, 100);
  };

  const handleStatsUpdate = (stats: GameResult) => {
      currentStatsRef.current = stats;
  };

  const handleFinish = (res: GameResult) => {
      if (raceIntervalRef.current) clearInterval(raceIntervalRef.current);
      if (clockIntervalRef.current) clearInterval(clockIntervalRef.current);

      const allParticipants = [
          { id: 'user', name: user?.username || 'You', wpm: res.wpm, accuracy: res.accuracy, isUser: true },
          ...opponents.map(bot => ({ id: bot.id, name: bot.name, wpm: bot.wpm, accuracy: bot.accuracy, isUser: false }))
      ];

      allParticipants.sort((a, b) => b.wpm - a.wpm);
      const rankedResults: LeaderboardEntry[] = allParticipants.map((p, index) => ({ ...p, rank: index + 1 }));
      setLeaderboard(rankedResults);

      const userRank = rankedResults.find(p => p.isUser)?.rank || rankedResults.length;
      const isWin = userRank === 1;

      setPlayerWon(isWin);
      setStatus('FINISHED');
      setResult(res);

      addMatch({
        id: Date.now().toString(),
        date: new Date().toISOString(),
        wpm: res.wpm,
        accuracy: res.accuracy,
        mode: GameMode.MULTIPLAYER,
        difficulty: getDifficultyByRank(user?.rank),
        errors: res.errors
      }, isWin ? 300 : 150, isWin); 
  };

  const copyInviteLink = () => {
      const baseUrl = window.location.origin + window.location.pathname;
      const userName = encodeURIComponent(user?.username || 'Player');
      const wpm = user?.avgWpm || 40;
      const url = `${baseUrl}#/multiplayer?lobby=${lobbyId}&host=${userName}&wpm=${wpm}`;
      navigator.clipboard.writeText(url).then(() => {
          setCopied(true);
          setTimeout(() => setCopied(false), 2000);
      });
  };

  if (lobbyMode === 'SELECT' && status === 'IDLE') {
      return (
          <div className="max-w-4xl mx-auto py-8">
              <h2 className="text-3xl font-bold text-center text-slate-800 dark:text-white mb-8 italic">MARATHON ARENA</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div onClick={handleStartRanked} className="group p-8 bg-white dark:bg-abyss rounded-xl border border-slate-200 dark:border-white/5 hover:border-neon-cyan cursor-pointer shadow-sm hover:shadow-xl hover:shadow-neon-cyan/10 transform transition-all duration-300 hover:-translate-y-2 text-center">
                      <Globe size={40} className="mx-auto mb-4 text-neon-cyan" />
                      <h3 className="text-xl font-bold text-slate-800 dark:text-white">Ranked Marathon</h3>
                      <p className="text-slate-500 mb-4">60-second endurance challenge.</p>
                      <span className="text-neon-cyan font-bold text-sm uppercase tracking-widest">Deploy Now</span>
                  </div>

                  <div onClick={handleCreateLobby} className="group p-8 bg-white dark:bg-abyss rounded-xl border border-slate-200 dark:border-white/5 hover:border-neon-purple cursor-pointer shadow-sm hover:shadow-xl hover:shadow-neon-purple/10 transform transition-all duration-300 hover:-translate-y-2 text-center">
                      <Lock size={40} className="mx-auto mb-4 text-neon-purple" />
                      <h3 className="text-xl font-bold text-slate-800 dark:text-white">Timed Duel</h3>
                      <p className="text-slate-500 mb-4">Challenge a real player to 1 minute.</p>
                      <span className="text-neon-purple font-bold text-sm uppercase tracking-widest">Establish Room</span>
                  </div>
              </div>
          </div>
      );
  }

  if (lobbyMode === 'JOINED' && status === 'IDLE') {
    return (
        <div className="max-w-md mx-auto py-12 text-center space-y-6">
            <div className="p-6 bg-neon-cyan/10 border border-neon-cyan/20 rounded-2xl">
                <Users size={32} className="mx-auto text-neon-cyan mb-3" />
                <h2 className="text-2xl font-bold text-slate-800 dark:text-white">Arena Joined</h2>
                <p className="text-slate-500">Host: <span className="text-neon-cyan font-bold">{hostName}</span></p>
            </div>
            <div className="flex flex-col items-center gap-2">
                <Loader2 className="animate-spin text-neon-cyan" size={24} />
                <p className="text-sm font-mono text-slate-400 uppercase tracking-widest">Syncing clocks with host...</p>
            </div>
        </div>
    );
  }

  if (lobbyMode === 'HOSTING' && status === 'IDLE') {
      return (
          <div className="max-w-lg mx-auto py-12 text-center">
              <h2 className="text-2xl font-bold text-slate-800 dark:text-white mb-6">Awaiting Challenger</h2>

              <div className="bg-slate-100 dark:bg-white/5 p-4 rounded-xl flex items-center gap-3 mb-8 border border-slate-200 dark:border-white/10">
                  <div className="flex-1 text-xs text-slate-500 truncate text-left font-mono">Invite link for 1-minute duel...</div>
                  <button onClick={copyInviteLink} className="flex items-center gap-2 px-4 py-2 bg-neon-purple text-white rounded-lg font-bold hover:scale-105 active:scale-95 transition-transform text-xs">
                      {copied ? <Check size={14} /> : <Copy size={14} />}
                      {copied ? 'COPIED' : 'COPY'}
                  </button>
              </div>

              <div className={`p-6 rounded-2xl border-2 mb-8 transition-colors ${peerCount >= 2 ? 'border-neon-green/30 bg-neon-green/5' : 'border-slate-200 dark:border-white/5 bg-slate-50 dark:bg-white/5'}`}>
                  <div className="flex justify-center -space-x-4 mb-4">
                      <div className="w-12 h-12 rounded-full bg-neon-purple border-4 border-white dark:border-abyss flex items-center justify-center text-white"><UserIcon size={20}/></div>
                      <div className={`w-12 h-12 rounded-full border-4 border-white dark:border-abyss flex items-center justify-center transition-colors ${peerCount >= 2 ? 'bg-neon-cyan text-white' : 'bg-slate-200 dark:bg-slate-800 text-slate-400'}`}>
                        {peerCount >= 2 ? <UserIcon size={20}/> : <span className="text-xs">?</span>}
                      </div>
                  </div>
                  <p className={`text-sm font-bold uppercase tracking-widest ${peerCount >= 2 ? 'text-neon-green' : 'text-slate-400'}`}>
                      {peerCount >= 2 ? 'Challenger Ready' : 'Awaiting Peer...'}
                  </p>
              </div>

              <button 
                disabled={peerCount < 2}
                onClick={startPrivateMatch}
                className={`w-full py-4 font-black rounded-xl mb-4 transition-all shadow-xl ${
                    peerCount >= 2 
                    ? 'bg-neon-green text-white hover:scale-[1.02] shadow-neon-green/20' 
                    : 'bg-slate-200 dark:bg-white/5 text-slate-400 cursor-not-allowed'
                }`}
              >
                  {peerCount >= 2 ? 'START 60s RACE' : 'PEER REQUIRED'}
              </button>
          </div>
      );
  }

  if (status === 'SEARCHING') {
      return (
          <div className="flex flex-col items-center justify-center h-64">
              <Loader2 className="animate-spin text-neon-cyan mb-4" size={40} />
              <h2 className="text-xl font-bold text-slate-800 dark:text-white uppercase tracking-widest">{loadingText}</h2>
          </div>
      );
  }

  return (
    <div className="max-w-4xl mx-auto">
        <div className="mb-6 flex justify-between items-center border-b border-slate-200 dark:border-white/5 pb-4">
            <h2 className="text-xl font-bold text-slate-800 dark:text-white flex items-center gap-2">
                <Zap className="text-neon-cyan" size={20} /> Marathon Duel
            </h2>

            <div className={`flex items-center gap-3 px-6 py-2 rounded-full border-2 transition-all ${raceTimer <= 10 ? 'border-red-500 bg-red-500/10 animate-pulse' : 'border-neon-cyan bg-neon-cyan/5'}`}>
                <Timer size={20} className={raceTimer <= 10 ? 'text-red-500' : 'text-neon-cyan'} />
                <span className={`font-mono text-2xl font-black ${raceTimer <= 10 ? 'text-red-500' : 'text-slate-800 dark:text-white'}`}>
                    00:{raceTimer.toString().padStart(2, '0')}
                </span>
            </div>

            {status === 'COUNTDOWN' && (
                <div className="fixed inset-0 z-[60] flex items-center justify-center bg-black/40 backdrop-blur-sm">
                    <div className="text-[12rem] font-black text-white italic animate-pop-in drop-shadow-[0_0_50px_rgba(6,182,212,0.8)]">
                        {countdown > 0 ? countdown : 'GO!'}
                    </div>
                </div>
            )}
        </div>

        <TypingEngine 
            text={text}
            isGameActive={status === 'RACING'}
            onGameFinish={handleFinish}
            onStatsUpdate={handleStatsUpdate}
            opponents={opponents}
            isTimedMode={true}
        />

        {status === 'FINISHED' && result && (
            <div className="fixed inset-0 z-[70] flex items-center justify-center bg-black/90 backdrop-blur-md p-4">
                <div className="bg-white dark:bg-abyss p-8 rounded-3xl shadow-2xl max-w-lg w-full border border-slate-200 dark:border-white/10 animate-pop-in">
                    <h2 className={`text-5xl font-black text-center mb-8 italic tracking-tighter ${playerWon ? 'text-neon-cyan' : 'text-red-500'}`}>
                        {playerWon ? 'ARENA MASTER' : 'RANK DOWN'}
                    </h2>

                    <div className="space-y-3 mb-8">
                        {leaderboard.map((entry) => (
                            <div key={entry.id} className={`flex justify-between items-center p-4 rounded-xl border-2 transition-all ${entry.isUser ? 'border-neon-cyan bg-neon-cyan/10 scale-105 shadow-lg shadow-neon-cyan/20' : 'border-transparent bg-slate-50 dark:bg-white/5'}`}>
                                <div className="flex gap-4 items-center">
                                    <span className={`text-2xl font-black italic ${entry.rank === 1 ? 'text-yellow-400' : 'text-slate-400'}`}>#{entry.rank}</span>
                                    <span className={`font-bold ${entry.isUser ? 'text-neon-cyan' : 'text-slate-800 dark:text-white'}`}>{entry.name}</span>
                                </div>
                                <div className="text-right">
                                    <span className="block font-mono font-black text-xl">{entry.wpm} <span className="text-[10px] text-slate-500">WPM</span></span>
                                    <span className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">{entry.accuracy}% ACC</span>
                                </div>
                            </div>
                        ))}
                    </div>

                    <div className="flex gap-4">
                        <button onClick={() => window.location.reload()} className="flex-1 py-4 bg-neon-cyan text-black font-black rounded-xl hover:scale-105 active:scale-95 transition-transform shadow-lg shadow-neon-cyan/20 uppercase tracking-widest">Re-Queue</button>
                        <button onClick={() => navigate('/')} className="flex-1 py-4 bg-slate-100 dark:bg-white/10 text-slate-500 font-black rounded-xl hover:bg-slate-200 transition-colors uppercase tracking-widest">Dismiss</button>
                    </div>
                </div>
            </div>
        )}
    </div>
  );
};

export default Multiplayer;

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { GameResult, Opponent } from '../types';
import { Clock, Activity, Target, Zap } from 'lucide-react';

interface TypingEngineProps {
  text: string;
  isGameActive: boolean;
  onGameFinish: (result: GameResult) => void;
  onStatsUpdate?: (result: GameResult) => void;
  opponents?: Opponent[];
  isTimedMode?: boolean;
}

const TypingEngine: React.FC<TypingEngineProps> = ({ 
  text, 
  isGameActive, 
  onGameFinish, 
  onStatsUpdate,
  opponents, 
  isTimedMode = false 
}) => {
  const [input, setInput] = useState('');
  const [startTime, setStartTime] = useState<number | null>(null);
  const [currentTime, setCurrentTime] = useState(0); 
  const [wpm, setWpm] = useState(0);
  const [accuracy, setAccuracy] = useState(100);
  const inputRef = useRef<HTMLInputElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const [errors, setErrors] = useState(0);
  const [missedChars, setMissedChars] = useState<Record<string, number>>({});

  useEffect(() => {
    if (isGameActive) {
      setInput('');
      setStartTime(null);
      setCurrentTime(0);
      setWpm(0);
      setAccuracy(100);
      setErrors(0);
      setMissedChars({});
      setTimeout(() => inputRef.current?.focus(), 100);
    }
  }, [isGameActive, text]);

  const handleContainerClick = () => {
    inputRef.current?.focus();
  };

  const calculateStats = useCallback((finalInput?: string) => {
    if (!startTime) return null;
    const currentInput = finalInput !== undefined ? finalInput : input;
    const timeElapsedMin = (Date.now() - startTime) / 60000;
    const wordsTyped = currentInput.length / 5;
    const currentWpm = timeElapsedMin > 0.01 ? Math.round(wordsTyped / timeElapsedMin) : 0;
    const totalChars = currentInput.length;
    const netAccuracy = totalChars > 0 
        ? Math.max(0, Math.round(((totalChars - errors) / totalChars) * 100))
        : 100;

    const stats: GameResult = { 
        wpm: currentWpm, 
        accuracy: netAccuracy, 
        errors, 
        timeTaken: timeElapsedMin * 60, 
        characterStats: missedChars 
    };

    setWpm(currentWpm);
    setAccuracy(netAccuracy);

    if (onStatsUpdate) {
        onStatsUpdate(stats);
    }

    return stats;
  }, [input, startTime, errors, missedChars, onStatsUpdate]);

  useEffect(() => {
    const interval = setInterval(() => {
        if (startTime && isGameActive) {
            calculateStats();
        }
    }, 500);
    return () => clearInterval(interval);
  }, [startTime, isGameActive, calculateStats]);

  useEffect(() => {
    let animationFrameId: number;
    const updateTimer = () => {
        if (startTime && isGameActive) {
            setCurrentTime(Date.now() - startTime);
            animationFrameId = requestAnimationFrame(updateTimer);
        }
    };
    if (startTime && isGameActive) {
        animationFrameId = requestAnimationFrame(updateTimer);
    }
    return () => cancelAnimationFrame(animationFrameId);
  }, [startTime, isGameActive]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (!isGameActive) return;
    const val = e.target.value;

    // Safety check: don't allow typing beyond buffer
    if (val.length > text.length) return;

    if (!startTime && val.length === 1) {
        setStartTime(Date.now());
    }

    if (val.length > input.length) {
        const charIndex = val.length - 1;
        const typedChar = val[charIndex];
        const expectedChar = text[charIndex];
        if (typedChar !== expectedChar) {
             setErrors(prev => prev + 1);
             setMissedChars(prev => ({ ...prev, [expectedChar]: (prev[expectedChar] || 0) + 1 }));
        }
    }

    setInput(val);

    // Auto-finish only if NOT in timed mode
    if (!isTimedMode && val.length === text.length) {
        const finalStats = calculateStats(val);
        if (finalStats) onGameFinish(finalStats);
    }
  };

  const renderText = () => {
    // Only render a window of text to keep performance high for marathon mode
    const startIndex = Math.max(0, input.length - 100);
    const endIndex = Math.min(text.length, input.length + 300);
    const visibleText = text.split('').slice(startIndex, endIndex);

    return visibleText.map((char, localIndex) => {
      const index = localIndex + startIndex;
      let className = "font-mono text-2xl lg:text-3xl transition-colors duration-75 relative ";
      const typedChar = input[index];

      if (index === input.length) {
        className += "bg-neon-purple/20 border-b-2 border-neon-purple text-slate-900 dark:text-white ";
        return <span key={index} className={className}>{char}</span>;
      } else if (index < input.length) {
        if (typedChar === char) {
          className += "text-neon-cyan ";
        } else {
          className += "text-red-500 bg-red-100 dark:bg-red-900/20 ";
        }
      } else {
        className += "text-slate-300 dark:text-slate-600 ";
      }
      return <span key={index} className={className}>{char}</span>;
    });
  };

  const renderOpponents = () => {
    if (!opponents) return null;
    return (
        <div className="mb-6 space-y-4 p-4 bg-slate-50 dark:bg-white/5 rounded-lg border border-slate-200 dark:border-white/5">
             <div className="space-y-1">
                 <div className="flex justify-between items-end text-xs font-bold uppercase tracking-wider text-neon-cyan">
                    <span>YOU</span>
                    <span className="font-mono">{wpm} WPM</span>
                 </div>
                 <div className="relative h-2 bg-slate-200 dark:bg-slate-700 rounded-full overflow-hidden">
                     <div 
                        className="absolute top-0 left-0 h-full bg-neon-cyan transition-all duration-300 ease-linear"
                        style={{ width: `${(input.length / text.length) * 100}%` }}
                     ></div>
                 </div>
             </div>
             {opponents.map(opp => (
                <div key={opp.id} className="space-y-1">
                    <div className="flex justify-between items-end text-xs font-bold uppercase tracking-wider text-slate-500">
                        <span>{opp.name}</span>
                        <span className="font-mono">{opp.wpm} WPM</span>
                    </div>
                    <div className="relative h-2 bg-slate-200 dark:bg-slate-700 rounded-full overflow-hidden">
                        <div 
                            className="absolute top-0 left-0 h-full transition-all duration-75 ease-linear"
                            style={{ 
                                width: `${opp.progress}%`, 
                                backgroundColor: opp.color
                            }}
                        ></div>
                    </div>
                </div>
             ))}
        </div>
    )
  }

  const formatSeconds = (ms: number) => Math.floor(ms / 1000).toString().padStart(2, '0');
  const formatMillis = (ms: number) => Math.floor((ms % 1000) / 10).toString().padStart(2, '0');

  return (
    <div className="w-full max-w-4xl mx-auto relative flex flex-col items-center">
      {/* Visual Gaming Timer HUD - Hidden in Timed Mode as parent shows it better */}
      {!isTimedMode && (
        <div className={`transition-all duration-500 transform ${startTime && isGameActive ? 'opacity-100 translate-y-0 scale-100' : 'opacity-0 -translate-y-4 scale-90'} mb-8 w-full`}>
            <div className="flex flex-col items-center">
                <div className="relative bg-slate-900/90 dark:bg-black/80 backdrop-blur-md px-10 py-3 rounded-2xl border-b-4 border-neon-cyan shadow-[0_0_20px_rgba(6,182,212,0.3)] flex items-center gap-6 group overflow-hidden">
                    <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/5 to-transparent -translate-x-full group-hover:animate-[shimmer_2s_infinite]"></div>

                    <div className="flex items-center gap-3">
                        <div className="p-2 bg-neon-cyan/20 rounded-lg animate-pulse">
                            <Clock size={24} className="text-neon-cyan" />
                        </div>
                        <div className="flex flex-col">
                            <span className="text-[10px] font-black uppercase tracking-[0.2em] text-slate-500">Time</span>
                            <span className="font-mono text-3xl font-black text-white tabular-nums">
                                {formatSeconds(currentTime)}<span className="text-neon-cyan animate-pulse">.</span>{formatMillis(currentTime)}
                            </span>
                        </div>
                    </div>

                    <div className="h-10 w-px bg-slate-700"></div>

                    <div className="flex items-center gap-3">
                         <div className="p-2 bg-neon-purple/20 rounded-lg">
                            <Activity size={24} className="text-neon-purple" />
                        </div>
                        <div className="flex flex-col">
                            <span className="text-[10px] font-black uppercase tracking-[0.2em] text-slate-500">Speed</span>
                            <span className="font-mono text-3xl font-black text-white tabular-nums">{wpm} <span className="text-xs text-neon-purple font-mono uppercase">WPM</span></span>
                        </div>
                    </div>
                </div>
            </div>
        </div>
      )}

      {/* Main Stats Card */}
      <div className={`w-full grid grid-cols-3 gap-1 mb-6 p-1 bg-slate-100 dark:bg-white/5 rounded-2xl border border-slate-200 dark:border-white/5 shadow-inner transition-all ${startTime && isGameActive ? 'opacity-50 scale-95 blur-[0.5px]' : 'opacity-100'}`}>
        <div className="bg-white dark:bg-abyss p-4 rounded-xl text-center flex flex-col items-center justify-center">
            <Zap size={14} className="text-neon-purple mb-1 opacity-50" />
            <p className="text-slate-500 text-[10px] uppercase font-black tracking-widest">WPM</p>
            <p className="text-3xl font-black text-slate-800 dark:text-white tabular-nums">{wpm}</p>
        </div>
        <div className="bg-white dark:bg-abyss p-4 rounded-xl text-center flex flex-col items-center justify-center">
            <Target size={14} className="text-neon-cyan mb-1 opacity-50" />
            <p className="text-slate-500 text-[10px] uppercase font-black tracking-widest">Accuracy</p>
            <p className={`text-3xl font-black tabular-nums ${accuracy >= 95 ? 'text-neon-green' : 'text-slate-800 dark:text-white'}`}>{accuracy}%</p>
        </div>
        <div className="bg-white dark:bg-abyss p-4 rounded-xl text-center flex flex-col items-center justify-center">
            <Activity size={14} className="text-red-500 mb-1 opacity-50" />
            <p className="text-slate-500 text-[10px] uppercase font-black tracking-widest">Errors</p>
            <p className="text-3xl font-black text-red-500 tabular-nums">{errors}</p>
        </div>
      </div>

      {renderOpponents()}

      <div 
        ref={containerRef}
        onClick={handleContainerClick}
        className="relative min-h-[280px] p-8 md:p-12 bg-white dark:bg-abyss rounded-3xl border border-slate-200 dark:border-white/10 cursor-text shadow-xl ring-4 ring-transparent focus-within:ring-neon-purple/10 transition-all w-full overflow-hidden"
      >
        <input 
            ref={inputRef}
            type="text"
            className="absolute opacity-0 top-0 left-0 w-full h-full cursor-default"
            value={input}
            onChange={handleChange}
            autoComplete="off"
            disabled={!isGameActive}
        />
        <div className="leading-relaxed select-none break-words whitespace-pre-wrap font-medium">
            {renderText()}
        </div>
        {!isGameActive && !startTime && text.length > 0 && (
            <div className="absolute inset-0 flex items-center justify-center bg-white/80 dark:bg-black/90 rounded-3xl z-10 animate-fade-in backdrop-blur-sm">
                <div className="flex flex-col items-center gap-4">
                    <div className="w-16 h-16 rounded-full bg-neon-purple/20 flex items-center justify-center animate-bounce">
                        <Zap className="text-neon-purple" size={32} />
                    </div>
                    <p className="text-xl font-black text-slate-800 dark:text-white tracking-widest uppercase">Strike Any Key to Start</p>
                    {isTimedMode && <p className="text-xs text-neon-cyan font-bold uppercase">60 Seconds on the Clock</p>}
                </div>
            </div>
        )}
      </div>
    </div>
  );
};

export default TypingEngine;

import { GameMode, GameResult, Difficulty, Opponent, Rank } from '../types';
import { generatePracticeText } from '../services/geminiService';
import TypingEngine from '../components/TypingEngine';
import { useAuth } from '../contexts/AuthContext';
import { supabase } from '../services/supabase';
import { Loader2, Zap, Check, Copy, Lock, Globe, Users, User as UserIcon, Timer } from 'lucide-react';
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';

const BOT_NAMES = ['Speedster_99', 'CodeNinja', 'TypeMaster_X', 'KeyboardWarrior', 'FingerSlippage', 'Glitch_Runner'];
const COLORS = ['#ef4444', '#f59e0b', '#10b981', '#8b5cf6', '#ec4899', '#06b6d4'];

interface LeaderboardEntry {
  id: string;
  rank: number;
  name: string;
  wpm: number;
  accuracy: number;
  isUser: boolean;
}

type LobbyMode = 'SELECT' | 'HOSTING' | 'JOINED' | 'SEARCHING';

const Multiplayer: React.FC = () => {
  const { addMatch, user } = useAuth();
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();

  const [lobbyMode, setLobbyMode] = useState<LobbyMode>('SELECT');
  const [status, setStatus] = useState<'IDLE' | 'SEARCHING' | 'COUNTDOWN' | 'RACING' | 'FINISHED'>('IDLE');
  const [text, setText] = useState('');
  const [opponents, setOpponents] = useState<Opponent[]>([]);
  const [countdown, setCountdown] = useState(3);
  const [raceTimer, setRaceTimer] = useState(60);
  const [result, setResult] = useState<GameResult | null>(null);
  const [playerWon, setPlayerWon] = useState(false);
  const [leaderboard, setLeaderboard] = useState<LeaderboardEntry[]>([]);

  // Added missing loadingText state
  const [loadingText, setLoadingText] = useState('Initializing Arena...');
  const [lobbyId, setLobbyId] = useState<string>('');
  const [hostName, setHostName] = useState<string>('');
  const [hostWpm, setHostWpm] = useState<number>(0);
  const [copied, setCopied] = useState(false);
  const [isPrivateMatch, setIsPrivateMatch] = useState(false);
  const [peerCount, setPeerCount] = useState(0);

  const raceIntervalRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const clockIntervalRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const channelRef = useRef<any>(null);
  const currentStatsRef = useRef<GameResult | null>(null);

  const getDifficultyByRank = (rank: Rank | undefined): Difficulty => {
    if (!rank) return Difficulty.EASY;
    const r = rank.toString();
    if (r.includes('Bronze') || r.includes('Silver')) return Difficulty.EASY;
    if (r.includes('Gold') || r.includes('Platinum')) return Difficulty.MEDIUM;
    return Difficulty.HARD;
  };

  // Realtime Channel Management
  useEffect(() => {
    if (!lobbyId || !user) return;

    const channel = supabase.channel(`lobby:${lobbyId}`, {
      config: {
        presence: { key: user.id },
      },
    });

    channel
      .on('presence', { event: 'sync' }, () => {
        const state = channel.presenceState();
        setPeerCount(Object.keys(state).length);
      })
      .on('broadcast', { event: 'START_RACE' }, (payload) => {
        if (lobbyMode === 'JOINED') {
            setText(payload.payload.text);
            setupOpponent(payload.payload.hostData);
            setStatus('COUNTDOWN');
        }
      })
      .subscribe(async (status) => {
        if (status === 'SUBSCRIBED') {
          await channel.track({
            id: user.id,
            username: user.username,
            avgWpm: user.avgWpm || 40,
            online_at: new Date().toISOString(),
          });
        }
      });

    channelRef.current = channel;

    return () => {
      channel.unsubscribe();
      channelRef.current = null;
    };
  }, [lobbyId, user?.id, lobbyMode]);

  useEffect(() => {
    const lobbyParam = searchParams.get('lobby');
    const hostParam = searchParams.get('host');
    const wpmParam = searchParams.get('wpm');
    const modeParam = searchParams.get('mode');

    if (lobbyParam) {
        setLobbyId(lobbyParam);
        setIsPrivateMatch(true);
        if (hostParam) {
            setLobbyMode('JOINED'); 
            setHostName(decodeURIComponent(hostParam));
            setHostWpm(wpmParam ? parseInt(wpmParam) : 40);
        } else {
            setLobbyMode('HOSTING'); 
        }
    } else if (modeParam === 'host') {
        handleCreateLobby();
    }
    return () => {
        if (raceIntervalRef.current) clearInterval(raceIntervalRef.current);
        if (clockIntervalRef.current) clearInterval(clockIntervalRef.current);
    };
  }, [searchParams]);

  const handleCreateLobby = () => {
      const newId = Math.random().toString(36).substring(7).toUpperCase();
      setLobbyId(newId);
      setIsPrivateMatch(true);
      setLobbyMode('HOSTING');
  };

  const handleStartRanked = () => {
      setIsPrivateMatch(false);
      setLobbyMode('SEARCHING');
      setLobbyId('GLOBAL_' + Math.random().toString(36).substring(7).toUpperCase());
      startMatchmaking(false);
  };

  const setupOpponent = (opponentData: { name: string, wpm: number }) => {
      const newOpponent: Opponent = {
          id: 'opponent-1',
          name: opponentData.name,
          progress: 0,
          wpm: opponentData.wpm,
          accuracy: 96,
          color: COLORS[0],
          isFinished: false
      };
      setOpponents([newOpponent]);
  };

  const startPrivateMatch = async () => {
    if (peerCount < 2) return; 

    setStatus('SEARCHING');
    setLoadingText("Generating Arena Text...");

    // Request a much longer text for marathon mode
    const difficulty = getDifficultyByRank(user?.rank);
    const generatedText = await generatePracticeText(difficulty);
    // Duplicate text to ensure it's long enough for 1 minute
    const marathonText = (generatedText + " ").repeat(5); 
    setText(marathonText);

    if (channelRef.current) {
        channelRef.current.send({
            type: 'broadcast',
            event: 'START_RACE',
            payload: { 
                text: marathonText,
                hostData: { name: user?.username, wpm: user?.avgWpm || 40 }
            }
        });
    }

    const presence = channelRef.current.presenceState();
    const otherUserId = Object.keys(presence).find(id => id !== user?.id);
    const otherUser = otherUserId ? presence[otherUserId][0] : null;

    setupOpponent({ 
        name: otherUser?.username || hostName || "Challenger", 
        wpm: otherUser?.avgWpm || hostWpm || 40 
    });

    setStatus('COUNTDOWN');
  };

  const startMatchmaking = async (isPrivate: boolean) => {
    setStatus('SEARCHING');
    setResult(null);
    setPlayerWon(false);
    setLeaderboard([]);
    setRaceTimer(60);

    setLoadingText("Initializing Server...");

    const difficulty = getDifficultyByRank(user?.rank);
    const generatedText = await generatePracticeText(difficulty);
    const marathonText = (generatedText + " ").repeat(5);
    setText(marathonText);

    const newOpponents: Opponent[] = [];
    const r = user?.rank || Rank.BRONZE_I;
    let minWpm = 20;
    let maxVar = 15;

    if (r.includes('Bronze')) { minWpm = 25; maxVar = 15; }
    else if (r.includes('Silver')) { minWpm = 40; maxVar = 20; }
    else if (r.includes('Gold')) { minWpm = 55; maxVar = 20; }
    else { minWpm = 80; maxVar = 30; }

    const numBots = Math.floor(Math.random() * 2) + 1; 

    for (let i = 0; i < numBots; i++) {
        newOpponents.push({
            id: `bot-${i}`,
            name: BOT_NAMES[Math.floor(Math.random() * BOT_NAMES.length)],
            progress: 0,
            wpm: Math.max(15, Math.floor(Math.random() * maxVar) + minWpm),
            accuracy: 94,
            color: COLORS[i % COLORS.length],
            isFinished: false
        });
    }
    setOpponents(newOpponents);
    setStatus('COUNTDOWN');
  };

  useEffect(() => {
    if (status === 'COUNTDOWN') {
        if (countdown > 0) {
            const timer = setTimeout(() => setCountdown(c => c - 1), 1000);
            return () => clearTimeout(timer);
        } else {
            setStatus('RACING');
            setRaceTimer(60);
            startBotRace();
            startRaceClock();
        }
    }
  }, [status, countdown]);

  const startRaceClock = () => {
      clockIntervalRef.current = setInterval(() => {
          setRaceTimer(prev => {
              if (prev <= 1) {
                  clearInterval(clockIntervalRef.current!);
                  handleRaceTimeout();
                  return 0;
              }
              return prev - 1;
          });
      }, 1000);
  };

  const handleRaceTimeout = () => {
      if (currentStatsRef.current) {
          handleFinish(currentStatsRef.current);
      } else {
          // Fallback if no stats were recorded yet
          handleFinish({ wpm: 0, accuracy: 100, errors: 0, timeTaken: 60, characterStats: {} });
      }
  };

  const startBotRace = () => {
      const startTime = Date.now();
      const textLength = text.length;
      raceIntervalRef.current = setInterval(() => {
          setOpponents(prev => {
              const now = Date.now();
              const elapsedMin = (now - startTime) / 60000;
              return prev.map(bot => {
                  const expectedChars = bot.wpm * 5 * elapsedMin;
                  const progress = Math.min(100, (expectedChars / textLength) * 100);
                  return { ...bot, progress };
              });
          });
      }, 100);
  };

  const handleStatsUpdate = (stats: GameResult) => {
      currentStatsRef.current = stats;
  };

  const handleFinish = (res: GameResult) => {
      if (raceIntervalRef.current) clearInterval(raceIntervalRef.current);
      if (clockIntervalRef.current) clearInterval(clockIntervalRef.current);

      const allParticipants = [
          { id: 'user', name: user?.username || 'You', wpm: res.wpm, accuracy: res.accuracy, isUser: true },
          ...opponents.map(bot => ({ id: bot.id, name: bot.name, wpm: bot.wpm, accuracy: bot.accuracy, isUser: false }))
      ];

      allParticipants.sort((a, b) => b.wpm - a.wpm);
      const rankedResults: LeaderboardEntry[] = allParticipants.map((p, index) => ({ ...p, rank: index + 1 }));
      setLeaderboard(rankedResults);

      const userRank = rankedResults.find(p => p.isUser)?.rank || rankedResults.length;
      const isWin = userRank === 1;

      setPlayerWon(isWin);
      setStatus('FINISHED');
      setResult(res);

      addMatch({
        id: Date.now().toString(),
        date: new Date().toISOString(),
        wpm: res.wpm,
        accuracy: res.accuracy,
        mode: GameMode.MULTIPLAYER,
        difficulty: getDifficultyByRank(user?.rank),
        errors: res.errors
      }, isWin ? 300 : 150, isWin); 
  };

  const copyInviteLink = () => {
      const baseUrl = window.location.origin + window.location.pathname;
      const userName = encodeURIComponent(user?.username || 'Player');
      const wpm = user?.avgWpm || 40;
      const url = `${baseUrl}#/multiplayer?lobby=${lobbyId}&host=${userName}&wpm=${wpm}`;
      navigator.clipboard.writeText(url).then(() => {
          setCopied(true);
          setTimeout(() => setCopied(false), 2000);
      });
  };

  if (lobbyMode === 'SELECT' && status === 'IDLE') {
      return (
          <div className="max-w-4xl mx-auto py-8">
              <h2 className="text-3xl font-bold text-center text-slate-800 dark:text-white mb-8 italic">MARATHON ARENA</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div onClick={handleStartRanked} className="group p-8 bg-white dark:bg-abyss rounded-xl border border-slate-200 dark:border-white/5 hover:border-neon-cyan cursor-pointer shadow-sm hover:shadow-xl hover:shadow-neon-cyan/10 transform transition-all duration-300 hover:-translate-y-2 text-center">
                      <Globe size={40} className="mx-auto mb-4 text-neon-cyan" />
                      <h3 className="text-xl font-bold text-slate-800 dark:text-white">Ranked Marathon</h3>
                      <p className="text-slate-500 mb-4">60-second endurance challenge.</p>
                      <span className="text-neon-cyan font-bold text-sm uppercase tracking-widest">Deploy Now</span>
                  </div>

                  <div onClick={handleCreateLobby} className="group p-8 bg-white dark:bg-abyss rounded-xl border border-slate-200 dark:border-white/5 hover:border-neon-purple cursor-pointer shadow-sm hover:shadow-xl hover:shadow-neon-purple/10 transform transition-all duration-300 hover:-translate-y-2 text-center">
                      <Lock size={40} className="mx-auto mb-4 text-neon-purple" />
                      <h3 className="text-xl font-bold text-slate-800 dark:text-white">Timed Duel</h3>
                      <p className="text-slate-500 mb-4">Challenge a real player to 1 minute.</p>
                      <span className="text-neon-purple font-bold text-sm uppercase tracking-widest">Establish Room</span>
                  </div>
              </div>
          </div>
      );
  }

  if (lobbyMode === 'JOINED' && status === 'IDLE') {
    return (
        <div className="max-w-md mx-auto py-12 text-center space-y-6">
            <div className="p-6 bg-neon-cyan/10 border border-neon-cyan/20 rounded-2xl">
                <Users size={32} className="mx-auto text-neon-cyan mb-3" />
                <h2 className="text-2xl font-bold text-slate-800 dark:text-white">Arena Joined</h2>
                <p className="text-slate-500">Host: <span className="text-neon-cyan font-bold">{hostName}</span></p>
            </div>
            <div className="flex flex-col items-center gap-2">
                <Loader2 className="animate-spin text-neon-cyan" size={24} />
                <p className="text-sm font-mono text-slate-400 uppercase tracking-widest">Syncing clocks with host...</p>
            </div>
        </div>
    );
  }

  if (lobbyMode === 'HOSTING' && status === 'IDLE') {
      return (
          <div className="max-w-lg mx-auto py-12 text-center">
              <h2 className="text-2xl font-bold text-slate-800 dark:text-white mb-6">Awaiting Challenger</h2>

              <div className="bg-slate-100 dark:bg-white/5 p-4 rounded-xl flex items-center gap-3 mb-8 border border-slate-200 dark:border-white/10">
                  <div className="flex-1 text-xs text-slate-500 truncate text-left font-mono">Invite link for 1-minute duel...</div>
                  <button onClick={copyInviteLink} className="flex items-center gap-2 px-4 py-2 bg-neon-purple text-white rounded-lg font-bold hover:scale-105 active:scale-95 transition-transform text-xs">
                      {copied ? <Check size={14} /> : <Copy size={14} />}
                      {copied ? 'COPIED' : 'COPY'}
                  </button>
              </div>

              <div className={`p-6 rounded-2xl border-2 mb-8 transition-colors ${peerCount >= 2 ? 'border-neon-green/30 bg-neon-green/5' : 'border-slate-200 dark:border-white/5 bg-slate-50 dark:bg-white/5'}`}>
                  <div className="flex justify-center -space-x-4 mb-4">
                      <div className="w-12 h-12 rounded-full bg-neon-purple border-4 border-white dark:border-abyss flex items-center justify-center text-white"><UserIcon size={20}/></div>
                      <div className={`w-12 h-12 rounded-full border-4 border-white dark:border-abyss flex items-center justify-center transition-colors ${peerCount >= 2 ? 'bg-neon-cyan text-white' : 'bg-slate-200 dark:bg-slate-800 text-slate-400'}`}>
                        {peerCount >= 2 ? <UserIcon size={20}/> : <span className="text-xs">?</span>}
                      </div>
                  </div>
                  <p className={`text-sm font-bold uppercase tracking-widest ${peerCount >= 2 ? 'text-neon-green' : 'text-slate-400'}`}>
                      {peerCount >= 2 ? 'Challenger Ready' : 'Awaiting Peer...'}
                  </p>
              </div>

              <button 
                disabled={peerCount < 2}
                onClick={startPrivateMatch}
                className={`w-full py-4 font-black rounded-xl mb-4 transition-all shadow-xl ${
                    peerCount >= 2 
                    ? 'bg-neon-green text-white hover:scale-[1.02] shadow-neon-green/20' 
                    : 'bg-slate-200 dark:bg-white/5 text-slate-400 cursor-not-allowed'
                }`}
              >
                  {peerCount >= 2 ? 'START 60s RACE' : 'PEER REQUIRED'}
              </button>
          </div>
      );
  }

  if (status === 'SEARCHING') {
      return (
          <div className="flex flex-col items-center justify-center h-64">
              <Loader2 className="animate-spin text-neon-cyan mb-4" size={40} />
              <h2 className="text-xl font-bold text-slate-800 dark:text-white uppercase tracking-widest">{loadingText}</h2>
          </div>
      );
  }

  return (
    <div className="max-w-4xl mx-auto">
        <div className="mb-6 flex justify-between items-center border-b border-slate-200 dark:border-white/5 pb-4">
            <h2 className="text-xl font-bold text-slate-800 dark:text-white flex items-center gap-2">
                <Zap className="text-neon-cyan" size={20} /> Marathon Duel
            </h2>

            <div className={`flex items-center gap-3 px-6 py-2 rounded-full border-2 transition-all ${raceTimer <= 10 ? 'border-red-500 bg-red-500/10 animate-pulse' : 'border-neon-cyan bg-neon-cyan/5'}`}>
                <Timer size={20} className={raceTimer <= 10 ? 'text-red-500' : 'text-neon-cyan'} />
                <span className={`font-mono text-2xl font-black ${raceTimer <= 10 ? 'text-red-500' : 'text-slate-800 dark:text-white'}`}>
                    00:{raceTimer.toString().padStart(2, '0')}
                </span>
            </div>

            {status === 'COUNTDOWN' && (
                <div className="fixed inset-0 z-[60] flex items-center justify-center bg-black/40 backdrop-blur-sm">
                    <div className="text-[12rem] font-black text-white italic animate-pop-in drop-shadow-[0_0_50px_rgba(6,182,212,0.8)]">
                        {countdown > 0 ? countdown : 'GO!'}
                    </div>
                </div>
            )}
        </div>

        <TypingEngine 
            text={text}
            isGameActive={status === 'RACING'}
            onGameFinish={handleFinish}
            onStatsUpdate={handleStatsUpdate}
            opponents={opponents}
            isTimedMode={true}
        />

        {status === 'FINISHED' && result && (
            <div className="fixed inset-0 z-[70] flex items-center justify-center bg-black/90 backdrop-blur-md p-4">
                <div className="bg-white dark:bg-abyss p-8 rounded-3xl shadow-2xl max-w-lg w-full border border-slate-200 dark:border-white/10 animate-pop-in">
                    <h2 className={`text-5xl font-black text-center mb-8 italic tracking-tighter ${playerWon ? 'text-neon-cyan' : 'text-red-500'}`}>
                        {playerWon ? 'ARENA MASTER' : 'RANK DOWN'}
                    </h2>

                    <div className="space-y-3 mb-8">
                        {leaderboard.map((entry) => (
                            <div key={entry.id} className={`flex justify-between items-center p-4 rounded-xl border-2 transition-all ${entry.isUser ? 'border-neon-cyan bg-neon-cyan/10 scale-105 shadow-lg shadow-neon-cyan/20' : 'border-transparent bg-slate-50 dark:bg-white/5'}`}>
                                <div className="flex gap-4 items-center">
                                    <span className={`text-2xl font-black italic ${entry.rank === 1 ? 'text-yellow-400' : 'text-slate-400'}`}>#{entry.rank}</span>
                                    <span className={`font-bold ${entry.isUser ? 'text-neon-cyan' : 'text-slate-800 dark:text-white'}`}>{entry.name}</span>
                                </div>
                                <div className="text-right">
                                    <span className="block font-mono font-black text-xl">{entry.wpm} <span className="text-[10px] text-slate-500">WPM</span></span>
                                    <span className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">{entry.accuracy}% ACC</span>
                                </div>
                            </div>
                        ))}
                    </div>

                    <div className="flex gap-4">
                        <button onClick={() => window.location.reload()} className="flex-1 py-4 bg-neon-cyan text-black font-black rounded-xl hover:scale-105 active:scale-95 transition-transform shadow-lg shadow-neon-cyan/20 uppercase tracking-widest">Re-Queue</button>
                        <button onClick={() => navigate('/')} className="flex-1 py-4 bg-slate-100 dark:bg-white/10 text-slate-500 font-black rounded-xl hover:bg-slate-200 transition-colors uppercase tracking-widest">Dismiss</button>
                    </div>
                </div>
            </div>
        )}
    </div>
  );
};

export default Multiplayer;

import React, { createContext, useContext, useState, useEffect } from 'react';
import { User, Rank, MatchStats, GameMode } from '../types';
import { getRankFromXP } from '../constants';
import { supabase, DbUser } from '../services/supabase';
import { getMatchHistory, saveMatchHistory } from '../services/matchService';

interface LevelUpEvent {
  oldRank: Rank;
  newRank: Rank;
}

interface AuthContextType {
  user: User | null;
  loading: boolean;
  levelUpEvent: LevelUpEvent | null;
  dismissLevelUp: () => void;
  login: (email: string, pass: string) => Promise<void>;
  signup: (email: string, pass: string, username: string) => Promise<void>;
  guestLogin: () => void;
  logout: () => Promise<void>;
  addMatch: (stats: MatchStats, baseXp: number, isMultiplayerWin?: boolean) => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const [levelUpEvent, setLevelUpEvent] = useState<LevelUpEvent | null>(null);

  useEffect(() => {
    let mounted = true;

    const initAuth = async () => {
      try {
        const { data: { session } } = await supabase.auth.getSession();
        if (session?.user && mounted) {
          await loadUserProfile(session.user.id);
        } else if (mounted) {
          const isGuest = localStorage.getItem('typearena_is_guest') === 'true';
          if (isGuest) {
            guestLogin();
          } else {
            setLoading(false);
          }
        }
      } catch (err) {
        console.error("Auth init failed:", err);
        if (mounted) setLoading(false);
      }
    };

    initAuth();

    const { data: { subscription } } = supabase.auth.onAuthStateChange(async (event, session) => {
      if (!mounted) return;

      if (event === 'SIGNED_IN' && session?.user) {
        setLoading(true);
        await loadUserProfile(session.user.id);
      } else if (event === 'SIGNED_OUT') {
        if (localStorage.getItem('typearena_is_guest') !== 'true') {
          setUser(null);
        }
        setLoading(false);
      }
    });

    return () => {
      mounted = false;
      subscription.unsubscribe();
    };
  }, []);

  async function loadUserProfile(userId: string, retries = 3) {
    try {
      const { data, error } = await supabase
        .from('users')
        .select('*')
        .eq('id', userId)
        .single();

      if (error || !data) {
        if (retries > 0) {
          console.log(`Profile not found for ${userId}, retrying... (${retries} left)`);
          await new Promise(res => setTimeout(res, 1500));
          return loadUserProfile(userId, retries - 1);
        }
        console.warn("Profile not found after retries:", userId, error);
        setUser(null);
        return;
      }

      const matches = await getMatchHistory(userId);
      const dbUser = data as DbUser;

      setUser({
        id: dbUser.id,
        username: dbUser.username || "Player",
        isGuest: false,
        xp: Number(dbUser.xp) || 0,
        rank: (dbUser.rank as Rank) || Rank.BRONZE_I,
        bestWpm: Number(dbUser.best_wpm) || 0,
        avgWpm: Number(dbUser.average_wpm) || 0,
        winStreak: Number(dbUser.win_streak) || 0,
        matches: matches
      });
    } catch (err) {
      console.error('Failed to load profile:', err);
      setUser(null);
    } finally {
      setLoading(false);
    }
  }

  const login = async (email: string, pass: string) => {
    setLoading(true);
    const { error } = await supabase.auth.signInWithPassword({ email, password: pass });
    if (error) {
      setLoading(false);
      throw error;
    }
  };

  const signup = async (email: string, pass: string, username: string) => {
    setLoading(true);
    const { data, error } = await supabase.auth.signUp({ 
      email, 
      password: pass,
      options: { data: { username } }
    });

    if (error) {
      setLoading(false);
      throw error;
    }
    if (!data.user) {
      setLoading(false);
      throw new Error("Registration failed.");
    }
  };

  const guestLogin = () => {
    localStorage.setItem('typearena_is_guest', 'true');
    setUser({
      id: 'guest_' + Date.now(),
      username: 'Ghost_Typist',
      isGuest: true,
      xp: 0,
      rank: Rank.BRONZE_I,
      matches: [],
      bestWpm: 0,
      avgWpm: 0,
      winStreak: 0
    });
    setLoading(false);
  };

  const logout = async () => {
    await supabase.auth.signOut();
    localStorage.removeItem('typearena_is_guest');
    setUser(null);
  };

  const addMatch = async (stats: MatchStats, baseXp: number, isMultiplayerWin?: boolean) => {
    if (!user) return;

    let newStreak = user.winStreak || 0;
    let finalXp = baseXp;

    if (stats.mode === GameMode.MULTIPLAYER && typeof isMultiplayerWin !== 'undefined') {
        if (isMultiplayerWin) {
            newStreak += 1;
            if (newStreak > 1) {
                finalXp += Math.min(newStreak * 10, 100);
            }
        } else {
            newStreak = 0;
        }
    }

    const currentRank = user.rank;
    const newXp = user.xp + finalXp;
    const newRank = getRankFromXP(newXp);

    if (newRank !== currentRank) {
        setLevelUpEvent({ oldRank: currentRank, newRank: newRank });
    }

    const newMatches = [stats, ...user.matches].slice(0, 50);
    const newBest = Math.max(user.bestWpm, stats.wpm);
    const totalWpm = newMatches.reduce((acc, m) => acc + m.wpm, 0);
    const avg = newMatches.length > 0 ? Math.round(totalWpm / newMatches.length) : 0;

    const updatedUser: User = {
      ...user,
      xp: newXp,
      rank: newRank,
      matches: newMatches,
      bestWpm: newBest,
      avgWpm: avg,
      winStreak: newStreak
    };

    setUser(updatedUser);

    if (!user.isGuest) {
      try {
        await saveMatchHistory(user.id, stats);
        await supabase.from('users').update({
          xp: newXp,
          rank: newRank,
          best_wpm: newBest,
          average_wpm: avg,
          win_streak: newStreak,
          updated_at: new Date().toISOString()
        }).eq('id', user.id);
      } catch (err) {
        console.error("Supabase Update Error:", err);
      }
    }
  };

  const dismissLevelUp = () => setLevelUpEvent(null);

  return (
    <AuthContext.Provider value={{ user, loading, levelUpEvent, dismissLevelUp, login, signup, guestLogin, logout, addMatch }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) throw new Error('useAuth must be used within an AuthProvider');
  return context;
};

import React from 'react';
import { HashRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext';
