function determinePracticeResult(userWPM, userAccuracy, targetWPM = 40) {
  const achievedWPM = userWPM * (userAccuracy / 100);

  if (achievedWPM >= targetWPM) {
    return {
      result: 'success',
      message: 'TARGET ACHIEVED',
      didImprove: achievedWPM > userPersonalBest
    };
  } else {
    return {
      result: 'incomplete',
      message: 'KEEP PRACTICING',
      progress: (achievedWPM / targetWPM) * 100
    };
  }
