javascript// Track mistakes during race
class MistakeTracker {
  constructor() {
    this.mistakes = [];
    this.characterErrors = {};
    this.wordErrors = {};
    this.wpmHistory = [];
  }

  recordMistake(expectedChar, typedChar, position, word) {
    this.mistakes.push({
      expected: expectedChar,
      typed: typedChar,
      position: position,
      word: word,
      timestamp: Date.now()
    });

    // Count character confusion
    const errorKey = `${expectedChar}→${typedChar}`;
    this.characterErrors[errorKey] = (this.characterErrors[errorKey] || 0) + 1;

    // Track problematic words
    this.wordErrors[word] = (this.wordErrors[word] || 0) + 1;
  }

  recordWPM(wpm, timestamp) {
    this.wpmHistory.push({ wpm, timestamp });
  }

  getTopMistakes(limit = 5) {
    return Object.entries(this.characterErrors)
      .sort((a, b) => b[1] - a[1])
      .slice(0, limit)
      .map(([error, count]) => {
        const [expected, typed] = error.split('→');
        return { expected, typed, count };
      });
  }

  getProblematicWords(limit = 5) {
    return Object.entries(this.wordErrors)
      .sort((a, b) => b[1] - a[1])
      .slice(0, limit)
      .map(([word, count]) => ({ word, count }));
  }

  getWPMVariance() {
    const wpms = this.wpmHistory.map(h => h.wpm);
    const max = Math.max(...wpms);
    const min = Math.min(...wpms);
    return max - min;
  }
}

