javascriptfunction analyzePerformance(mistakeTracker, userStats) {
  const topMistakes = mistakeTracker.getTopMistakes(5);
  const suggestions = [];

  // Analyze character confusions
  topMistakes.forEach(mistake => {
    if (areKeysAdjacent(mistake.expected, mistake.typed)) {
      suggestions.push({
        type: 'accuracy',
        priority: 'high',
        message: `Practice '${mistake.expected}' and '${mistake.typed}' keys - you confused them ${mistake.count} times`,
        drill: generateDrillText(mistake.expected, mistake.typed)
      });
    }
  });

  // Check consistency
  const variance = mistakeTracker.getWPMVariance();
  if (variance > 30) {
    suggestions.push({
      type: 'consistency',
      priority: 'medium',
      message: `Your WPM varied by ${variance} - work on steady rhythm`,
      recommendation: 'Practice at 80% of max speed for smoother typing'
    });
  }

  // Check accuracy vs speed balance
  if (userStats.accuracy < 90 && userStats.wpm > 60) {
    suggestions.push({
      type: 'accuracy',
      priority: 'high',
      message: 'You\'re fast but making too many errors',
      recommendation: 'Slow down 10-15% and focus on accuracy'
    });
  }

  return {
    strengths: identifyStrengths(mistakeTracker, userStats),
    weaknesses: identifyWeaknesses(mistakeTracker, userStats),
    suggestions: suggestions,
    recommendedPractice: generatePracticeRoutine(suggestions)
  };
}

function generateDrillText(char1, char2) {
  const drills = {
    'e-r': 'The red deer ran there where her herd rested serenely',
    'i-o': 'Oil points to options for oiling joints in portions',
    't-y': 'Try to type twenty wittyyttrium treaty mystery'
  };

  return drills[`${char1}-${char2}`] || `Practice ${char1} and ${char2} repeatedly`;
}

