                <h1 className={`text-6xl md:text-8xl font-black italic tracking-tighter uppercase ${userRankPos === 1 ? 'text-neon-cyan drop-shadow-[0_0_20px_rgba(6,182,212,0.6)]' : userRankPos === 2 ? 'text-neon-green' : 'text-neon-pink'}`}>
