import { GameMode, GameResult, Difficulty, Opponent, Rank } from '../types';
import { generatePracticeText } from '../services/geminiService';
import TypingEngine from '../components/TypingEngine';
import ResultDashboard from '../components/ResultDashboard';
import { useAuth } from '../contexts/AuthContext';
import { supabase } from '../services/supabase';
import { Loader2, Zap, Check, Copy, Lock, Globe, Users, User as UserIcon, Timer } from 'lucide-react';
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';

const BOT_NAMES = ['Speedster_99', 'CodeNinja', 'TypeMaster_X', 'KeyboardWarrior', 'FingerSlippage', 'Glitch_Runner'];
const COLORS = ['#ef4444', '#f59e0b', '#10b981', '#8b5cf6', '#ec4899', '#06b6d4'];

const Multiplayer: React.FC = () => {
  const { addMatch, user } = useAuth();
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();

  const [lobbyMode, setLobbyMode] = useState<'SELECT' | 'HOSTING' | 'JOINED' | 'SEARCHING'>('SELECT');
  const [status, setStatus] = useState<'IDLE' | 'SEARCHING' | 'COUNTDOWN' | 'RACING' | 'FINISHED'>('IDLE');
  const [text, setText] = useState('');
  const [opponents, setOpponents] = useState<Opponent[]>([]);
  const [countdown, setCountdown] = useState(3);
  const [raceTimer, setRaceTimer] = useState(60);
  const [result, setResult] = useState<GameResult | null>(null);
  const [userRankPos, setUserRankPos] = useState(1);
  const [leaderboard, setLeaderboard] = useState<any[]>([]);

  const [loadingText, setLoadingText] = useState('Initializing Arena...');
  const [lobbyId, setLobbyId] = useState<string>('');
  const [hostName, setHostName] = useState<string>('');
  const [hostWpm, setHostWpm] = useState<number>(0);
  const [copied, setCopied] = useState(false);
  const [peerCount, setPeerCount] = useState(0);

  const raceIntervalRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const clockIntervalRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const channelRef = useRef<any>(null);
  const currentStatsRef = useRef<GameResult | null>(null);

  const calculateAdjustedWpm = (wpm: number, accuracy: number) => wpm * (accuracy / 100);

  const getDifficultyByRank = (rank: Rank | undefined): Difficulty => {
    if (!rank) return Difficulty.EASY;
    const r = rank.toString();
    if (r.includes('Bronze') || r.includes('Silver')) return Difficulty.EASY;
    if (r.includes('Gold') || r.includes('Platinum')) return Difficulty.MEDIUM;
    return Difficulty.HARD;
  };

  useEffect(() => {
    if (!lobbyId || !user) return;
    const channel = supabase.channel(`lobby:${lobbyId}`, { config: { presence: { key: user.id } } });
    channel
      .on('presence', { event: 'sync' }, () => setPeerCount(Object.keys(channel.presenceState()).length))
      .on('broadcast', { event: 'START_RACE' }, (p) => {
        if (lobbyMode === 'JOINED') {
          setText(p.payload.text);
          setupOpponent(p.payload.hostData);
          setStatus('COUNTDOWN');
        }
      })
      .subscribe();
    channelRef.current = channel;
    return () => { channel.unsubscribe(); channelRef.current = null; };
  }, [lobbyId, user?.id, lobbyMode]);

  useEffect(() => {
    const lobby = searchParams.get('lobby');
    const host = searchParams.get('host');
    const mode = searchParams.get('mode');
    if (lobby) {
      setLobbyId(lobby);
      if (host) {
        setLobbyMode('JOINED'); 
        setHostName(decodeURIComponent(host));
        setHostWpm(parseInt(searchParams.get('wpm') || '40'));
      } else {
        setLobbyMode('HOSTING');
      }
    } else if (mode === 'host') {
      handleCreateLobby();
    }
    return () => {
      if (raceIntervalRef.current) clearInterval(raceIntervalRef.current);
      if (clockIntervalRef.current) clearInterval(clockIntervalRef.current);
    };
  }, [searchParams]);

  const handleCreateLobby = () => {
    setLobbyId(Math.random().toString(36).substring(7).toUpperCase());
    setLobbyMode('HOSTING');
  };

  const setupOpponent = (data: { name: string, wpm: number }) => {
    setOpponents([{ id: 'opp', name: data.name, progress: 0, wpm: data.wpm, accuracy: 96, color: COLORS[0], isFinished: false }]);
  };

  const startMatchmaking = async () => {
    setStatus('SEARCHING');
    setLoadingText("Fetching Terrain...");
    const text = await generatePracticeText(getDifficultyByRank(user?.rank));
    setText((text + " ").repeat(5));
    const bots: Opponent[] = [];
    for (let i = 0; i < 2; i++) {
        bots.push({
            id: `bot-${i}`,
            name: BOT_NAMES[Math.floor(Math.random() * BOT_NAMES.length)],
            progress: 0,
            wpm: Math.max(20, Math.floor(Math.random() * 30) + (user?.avgWpm || 40) - 10),
            accuracy: 95,
            color: COLORS[i],
            isFinished: false
        });
    }
    setOpponents(bots);
    setStatus('COUNTDOWN');
  };

  useEffect(() => {
    if (status === 'COUNTDOWN') {
      if (countdown > 0) {
        const t = setTimeout(() => setCountdown(c => c - 1), 1000);
        return () => clearTimeout(t);
      } else {
        setStatus('RACING');
        startBotRace();
        startRaceClock();
      }
    }
  }, [status, countdown]);

  const startRaceClock = () => {
    clockIntervalRef.current = setInterval(() => {
      setRaceTimer(prev => {
        if (prev <= 1) {
          clearInterval(clockIntervalRef.current!);
          handleFinish(currentStatsRef.current || { wpm: 0, accuracy: 100, errors: 0, timeTaken: 60, totalChars: 0, backspaces: 0, rawWpm: 0, wpmHistory: [], confusionMap: {}, characterStats: {} });
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const startBotRace = () => {
    const start = Date.now();
    raceIntervalRef.current = setInterval(() => {
      setOpponents(prev => prev.map(bot => ({
        ...bot,
        progress: Math.min(100, (bot.wpm * 5 * ((Date.now() - start) / 60000) / text.length) * 100)
      })));
    }, 100);
  };

  const handleFinish = (res: GameResult) => {
    if (raceIntervalRef.current) clearInterval(raceIntervalRef.current);
    if (clockIntervalRef.current) clearInterval(clockIntervalRef.current);

    const userScore = calculateAdjustedWpm(res.wpm, res.accuracy);
    const results = [
      { id: 'user', name: user?.username || 'You', wpm: res.wpm, accuracy: res.accuracy, score: userScore, isUser: true },
      ...opponents.map(b => ({ id: b.id, name: b.name, wpm: b.wpm, accuracy: b.accuracy, score: calculateAdjustedWpm(b.wpm, b.accuracy), isUser: false }))
    ].sort((a, b) => b.score - a.score).map((p, i) => ({ ...p, rank: i + 1 }));

    setLeaderboard(results);
    const rank = results.find(p => p.isUser)?.rank || results.length;
    setUserRankPos(rank);
    setResult(res);
    setStatus('FINISHED');

    addMatch({
      id: Date.now().toString(),
      date: new Date().toISOString(),
      wpm: res.wpm,
      accuracy: res.accuracy,
      mode: GameMode.MULTIPLAYER,
      difficulty: getDifficultyByRank(user?.rank),
      errors: res.errors
    }, rank === 1 ? 300 : rank === 2 ? 150 : 75, rank === 1);
  };

  if (status === 'IDLE' && lobbyMode === 'SELECT') {
    return (
      <div className="max-w-4xl mx-auto py-12 text-center">
        <h2 className="text-4xl font-black italic mb-12">ARENA SELECTION</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div onClick={startMatchmaking} className="p-12 bg-white/5 border border-white/10 rounded-3xl hover:border-neon-cyan cursor-pointer transition-all hover:-translate-y-2">
            <Globe size={48} className="mx-auto mb-4 text-neon-cyan" />
            <h3 className="text-2xl font-bold">RANKED MARATHON</h3>
            <p className="text-slate-500 mt-2">60s Global Endurance</p>
          </div>
          <div onClick={handleCreateLobby} className="p-12 bg-white/5 border border-white/10 rounded-3xl hover:border-neon-purple cursor-pointer transition-all hover:-translate-y-2">
            <Lock size={48} className="mx-auto mb-4 text-neon-purple" />
            <h3 className="text-2xl font-bold">PRIVATE DUEL</h3>
            <p className="text-slate-500 mt-2">1v1 Invitation Only</p>
          </div>
        </div>
      </div>
    );
  }

  if (status === 'SEARCHING') {
      return (
          <div className="flex flex-col items-center justify-center h-96">
              <Loader2 className="animate-spin text-neon-cyan mb-6" size={64} />
              <h2 className="text-2xl font-black uppercase tracking-widest">{loadingText}</h2>
          </div>
      );
  }

  return (
    <div className="max-w-5xl mx-auto">
      {status === 'FINISHED' && result ? (
        <ResultDashboard 
            result={result} 
            userRankPos={userRankPos} 
            leaderboard={leaderboard} 
            playerName={user?.username || 'Player'}
            onAction={(a) => a === 'REPLAY' ? window.location.reload() : navigate('/')}
        />
      ) : (
        <>
            <div className="mb-8 flex justify-between items-center border-b border-white/10 pb-6">
                <div className="flex items-center gap-3">
                    <Zap className="text-neon-cyan" size={24} />
                    <h2 className="text-2xl font-black uppercase italic tracking-tighter">Marathon Arena</h2>
                </div>
                <div className={`px-8 py-3 rounded-2xl border-2 transition-all ${raceTimer <= 10 ? 'border-neon-pink bg-neon-pink/10 animate-pulse' : 'border-neon-cyan bg-white/5'}`}>
                    <span className={`font-mono text-3xl font-black ${raceTimer <= 10 ? 'text-neon-pink' : 'text-white'}`}>
                        00:{raceTimer.toString().padStart(2, '0')}
                    </span>
                </div>
            </div>

            <TypingEngine 
                text={text}
                isGameActive={status === 'RACING'}
                onGameFinish={handleFinish}
                onStatsUpdate={(s) => currentStatsRef.current = s}
                opponents={opponents}
                isTimedMode={true}
            />

            {status === 'COUNTDOWN' && (
                <div className="fixed inset-0 z-[110] flex items-center justify-center bg-black/60 backdrop-blur-md">
                    <div className="text-[15rem] font-black text-white italic animate-pop-in drop-shadow-[0_0_80px_rgba(6,182,212,0.8)]">
                        {countdown > 0 ? countdown : 'GO'}
                    </div>
                </div>
            )}
        </>
      )}
    </div>
  );
};

export default Multiplayer;

import React, { useState, useEffect } from 'react';
import { Difficulty, GameMode, GameResult } from '../types';
import { generatePracticeText } from '../services/geminiService';
import TypingEngine from '../components/TypingEngine';
import ResultDashboard from '../components/ResultDashboard';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { Loader2, Terminal } from 'lucide-react';

const SinglePlayer: React.FC = () => {
  const { addMatch, user } = useAuth();
  const navigate = useNavigate();
  const [difficulty, setDifficulty] = useState<Difficulty>(Difficulty.EASY);
  const [text, setText] = useState('');
  const [loading, setLoading] = useState(false);
  const [gameState, setGameState] = useState<'IDLE' | 'PLAYING' | 'FINISHED'>('IDLE');
  const [result, setResult] = useState<GameResult | null>(null);

  const loadGame = async () => {
    setLoading(true);
    setGameState('IDLE');
    setResult(null);
    const newText = await generatePracticeText(difficulty);
    setText(newText);
    setLoading(false);
    setGameState('PLAYING');
  };

  useEffect(() => {
    loadGame();
  }, [difficulty]);

  const handleFinish = async (res: GameResult) => {
    setResult(res);
    setGameState('FINISHED');
    addMatch({
        id: Date.now().toString(),
        date: new Date().toISOString(),
        wpm: res.wpm,
        accuracy: res.accuracy,
        mode: GameMode.SINGLE_PLAYER,
        difficulty: difficulty,
        errors: res.errors
    }, 50);
  };

  if (gameState === 'FINISHED' && result) {
      return (
        <ResultDashboard 
            result={result} 
            userRankPos={1} // Practice is always personal victory if finished
            leaderboard={[{ id: 'user', name: user?.username || 'You', wpm: result.wpm, accuracy: result.accuracy, rank: 1, isUser: true }]} 
            playerName={user?.username || 'Player'}
            onAction={(a) => a === 'REPLAY' ? loadGame() : navigate('/')}
        />
      );
  }

  return (
    <div className="max-w-5xl mx-auto space-y-8">
      <div className="flex flex-col md:flex-row justify-between items-center gap-6 border-b border-white/10 pb-8">
        <h2 className="text-3xl font-black uppercase italic tracking-tighter flex items-center gap-3">
            <Terminal className="text-neon-purple" size={32} /> Tactical Practice
        </h2>
        <div className="flex gap-2 p-1.5 bg-white/5 rounded-2xl border border-white/10">
            {Object.values(Difficulty).map((d) => (
                <button
                    key={d}
                    onClick={() => !loading && difficulty !== d && setDifficulty(d)}
                    disabled={loading}
                    className={`px-6 py-2.5 rounded-xl text-sm font-black transition-all uppercase tracking-widest ${
                        difficulty === d ? 'bg-neon-purple text-white shadow-lg shadow-neon-purple/20 scale-105' : 'text-slate-500 hover:text-white'
                    }`}
                >
                    {d}
                </button>
            ))}
        </div>
      </div>

      {loading ? (
        <div className="h-[400px] flex flex-col items-center justify-center bg-white/5 rounded-[2.5rem] border border-white/10 border-dashed">
            <Loader2 className="animate-spin text-neon-purple mb-4" size={48} />
            <p className="text-slate-500 font-mono text-sm tracking-widest uppercase">Synthesizing Challenge...</p>
        </div>
      ) : (
        <TypingEngine 
            text={text}
            isGameActive={gameState === 'PLAYING'}
            onGameFinish={handleFinish}
        />
      )}
    </div>
  );
};

