import React, { useState, useEffect, useRef, useCallback } from 'react';
import { GameResult, Opponent } from '../types';
import { Clock, Activity, Target, Zap, Keyboard, Monitor, Eye, EyeOff } from 'lucide-react';
import VirtualKeyboard from './VirtualKeyboard';

interface TypingEngineProps {
  text: string;
  isGameActive: boolean;
  onGameFinish: (result: GameResult) => void;
  onStatsUpdate?: (result: GameResult) => void;
  opponents?: Opponent[];
  isTimedMode?: boolean;
}

const TypingEngine: React.FC<TypingEngineProps> = ({ 
  text, 
  isGameActive, 
  onGameFinish, 
  onStatsUpdate,
  opponents, 
  isTimedMode = false 
}) => {
  const [input, setInput] = useState('');
  const [startTime, setStartTime] = useState<number | null>(null);
  const [currentTime, setCurrentTime] = useState(0); 
  const [wpm, setWpm] = useState(0);
  const [accuracy, setAccuracy] = useState(100);
  const [showKeyboard, setShowKeyboard] = useState(() => {
    const saved = localStorage.getItem('typearena_show_kb');
    return saved === 'true';
  });

  const [lastKeyPressed, setLastKeyPressed] = useState<string>('');
  const [lastInputCorrect, setLastInputCorrect] = useState<boolean>(true);

  const inputRef = useRef<HTMLInputElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const [errors, setErrors] = useState(0);
  const [backspaces, setBackspaces] = useState(0);
  const [missedChars, setMissedChars] = useState<Record<string, number>>({});
  const [confusionMap, setConfusionMap] = useState<Record<string, number>>({});
  const [wpmHistory, setWpmHistory] = useState<{ time: number; wpm: number }[]>([]);

  useEffect(() => {
    localStorage.setItem('typearena_show_kb', String(showKeyboard));
  }, [showKeyboard]);

  useEffect(() => {
    if (isGameActive) {
      setInput('');
      setStartTime(null);
      setCurrentTime(0);
      setWpm(0);
      setAccuracy(100);
      setErrors(0);
      setBackspaces(0);
      setMissedChars({});
      setConfusionMap({});
      setWpmHistory([]);
      setTimeout(() => inputRef.current?.focus(), 100);
    }
  }, [isGameActive, text]);

  const handleContainerClick = () => {
    inputRef.current?.focus();
  };

  const calculateStats = useCallback((finalInput?: string, overrideStartTime?: number) => {
    const effectiveStartTime = overrideStartTime || startTime;
    if (!effectiveStartTime) return null;

    const currentInput = finalInput !== undefined ? finalInput : input;
    const now = Date.now();
    const timeElapsedSec = (now - effectiveStartTime) / 1000;
    const timeElapsedMin = timeElapsedSec / 60;

    // Words typed: 5 characters per word is standard
    const wordsTyped = currentInput.length / 5;

    // Loosen WPM calculation threshold for immediate feedback
    const currentWpm = timeElapsedMin > 0.005 ? Math.round(wordsTyped / timeElapsedMin) : 0;
    const rawWpm = currentWpm;

    const totalChars = currentInput.length;
    const netAccuracy = totalChars > 0 
        ? Math.max(0, Math.round(((totalChars - errors) / totalChars) * 100))
        : 100;

    const stats: GameResult = { 
        wpm: currentWpm, 
        accuracy: netAccuracy, 
        errors, 
        timeTaken: timeElapsedSec, 
        totalChars,
        backspaces,
        rawWpm,
        wpmHistory,
        confusionMap,
        characterStats: missedChars 
    };

    return stats;
  }, [input, startTime, errors, backspaces, missedChars, confusionMap, wpmHistory]);

  // WPM Sampler for Graph
  useEffect(() => {
    if (!startTime || !isGameActive) return;

    const interval = setInterval(() => {
      const stats = calculateStats();
      if (stats) {
        setWpmHistory(prev => [
          ...prev, 
          { time: Math.round((Date.now() - startTime) / 1000), wpm: stats.wpm }
        ]);
        setWpm(stats.wpm);
        setAccuracy(stats.accuracy);
      }
    }, 2000);

    return () => clearInterval(interval);
  }, [startTime, isGameActive, calculateStats]);

  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === 'Backspace') {
        setBackspaces(prev => prev + 1);
        setLastKeyPressed('Backspace');
      } else if (e.key.length === 1 || e.key === 'Enter' || e.key === ' ') {
        setLastKeyPressed(e.key);
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  const processInput = (newVal: string) => {
    if (!isGameActive) return;
    if (newVal.length > text.length) return;

    let currentStartTime = startTime;
    if (!startTime && newVal.length === 1) {
        currentStartTime = Date.now();
        setStartTime(currentStartTime);
    }

    if (newVal.length > input.length) {
        const charIndex = newVal.length - 1;
        const typedChar = newVal[charIndex];
        const expectedChar = text[charIndex];

        const match = typedChar === expectedChar;
        setLastInputCorrect(match);

        if (!match) {
             setErrors(prev => prev + 1);
             setMissedChars(prev => ({ ...prev, [expectedChar]: (prev[expectedChar] || 0) + 1 }));

             const confusionKey = `${expectedChar.toLowerCase()}→${typedChar.toLowerCase()}`;
             setConfusionMap(prev => ({
                ...prev,
                [confusionKey]: (prev[confusionKey] || 0) + 1
             }));
        }
    }

    setInput(newVal);

    // CRITICAL: Call parent update immediately on every keystroke
    // Use the local currentStartTime to avoid waiting for state update cycle
    const latestStats = calculateStats(newVal, currentStartTime);
    if (latestStats) {
        setWpm(latestStats.wpm);
        setAccuracy(latestStats.accuracy);
        if (onStatsUpdate) onStatsUpdate(latestStats);

        // Handle race completion (text finished)
        if (newVal.length === text.length) {
            onGameFinish(latestStats);
        }
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    processInput(e.target.value);
  };

  const handleVirtualKeyPress = (key: string) => {
    if (!isGameActive) return;
    if (key === 'Backspace') {
      processInput(input.slice(0, -1));
    } else if (key.length === 1 || key === ' ') {
      processInput(input + key);
    }
    setLastKeyPressed(key);
    setTimeout(() => setLastKeyPressed(''), 150);
  };

  const renderText = () => {
    const startIndex = Math.max(0, input.length - 100);
    const endIndex = Math.min(text.length, input.length + 300);
    const visibleText = text.split('').slice(startIndex, endIndex);

    return visibleText.map((char, localIndex) => {
      const index = localIndex + startIndex;
      let className = "font-mono text-2xl lg:text-3xl transition-colors duration-75 relative ";
      const typedChar = input[index];

      if (index === input.length) {
        className += "bg-neon-cyan/20 border-b-2 border-neon-cyan text-slate-900 dark:text-white ";
        return <span key={index} className={className}>{char}</span>;
      } else if (index < input.length) {
        if (typedChar === char) {
          className += "text-neon-cyan ";
        } else {
          className += "text-red-500 bg-red-100 dark:bg-red-900/20 ";
        }
      } else {
        className += "text-slate-300 dark:text-slate-600 ";
      }
      return <span key={index} className={className}>{char}</span>;
    });
  };

  return (
    <div className="w-full max-w-5xl mx-auto relative flex flex-col items-center">

      {/* HUD Header */}
      <div className="w-full flex justify-between items-end mb-6 px-4">
        <div className="flex gap-4">
            <div className="bg-slate-900/80 p-3 rounded-2xl border border-white/10">
                <p className="text-[10px] font-black uppercase text-slate-500 tracking-widest mb-1">Speed</p>
                <p className="text-2xl font-mono font-black text-neon-purple">{wpm} <span className="text-xs">WPM</span></p>
            </div>
            <div className="bg-slate-900/80 p-3 rounded-2xl border border-white/10">
                <p className="text-[10px] font-black uppercase text-slate-500 tracking-widest mb-1">Accuracy</p>
                <p className="text-2xl font-mono font-black text-neon-cyan">{accuracy}%</p>
            </div>
        </div>

        <button 
          onClick={() => setShowKeyboard(!showKeyboard)}
          className={`flex items-center gap-2 px-4 py-2 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all border ${showKeyboard ? 'bg-neon-cyan/10 border-neon-cyan text-neon-cyan' : 'bg-white/5 border-white/10 text-slate-500'}`}
        >
          {showKeyboard ? <Eye size={16} /> : <EyeOff size={16} />}
          {showKeyboard ? 'Hide Virtual Keyboard' : 'Show Virtual Keyboard'}
        </button>
      </div>

      <div 
        ref={containerRef}
        onClick={handleContainerClick}
        className="relative min-h-[200px] p-8 md:p-12 bg-white/5 backdrop-blur-md rounded-[2.5rem] border-2 border-white/10 cursor-text shadow-2xl w-full overflow-hidden transition-all focus-within:border-neon-cyan/50"
      >
        <input 
            ref={inputRef}
            type="text"
            className="absolute opacity-0 top-0 left-0 w-full h-full cursor-default"
            value={input}
            onChange={handleChange}
            onPaste={(e) => e.preventDefault()}
            autoComplete="off"
            disabled={!isGameActive}
        />
        <div className="leading-relaxed select-none break-words whitespace-pre-wrap font-medium">
            {renderText()}
        </div>
        {!isGameActive && !startTime && text.length > 0 && (
            <div className="absolute inset-0 flex items-center justify-center bg-black/60 rounded-[2.5rem] z-10 backdrop-blur-sm">
                <div className="flex flex-col items-center gap-4">
                    <Zap className="text-neon-cyan animate-bounce" size={48} />
                    <p className="text-2xl font-black text-white tracking-widest uppercase italic">Ready to Race?</p>
                </div>
            </div>
        )}
      </div>

      {showKeyboard && (
        <VirtualKeyboard 
          onKeyPress={handleVirtualKeyPress} 
          targetKey={text[input.length]} 
          lastPressedKey={lastKeyPressed}
          isCorrect={lastInputCorrect}
        />
      )}
    </div>
  );
};

export default TypingEngine;

import { GameMode, GameResult, Difficulty, Opponent, Rank } from '../types';
import { generatePracticeText } from '../services/geminiService';
import TypingEngine from '../components/TypingEngine';
import ResultDashboard from '../components/ResultDashboard';
import { useAuth } from '../contexts/AuthContext';
import { supabase } from '../services/supabase';
import { Loader2, Zap, Check, Copy, Lock, Globe, Users, User as UserIcon, Timer } from 'lucide-react';
import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';

const BOT_NAMES = ['Speedster_99', 'CodeNinja', 'TypeMaster_X', 'KeyboardWarrior', 'FingerSlippage', 'Glitch_Runner'];
const COLORS = ['#ef4444', '#f59e0b', '#10b981', '#8b5cf6', '#ec4899', '#06b6d4'];

const Multiplayer: React.FC = () => {
  const { addMatch, user } = useAuth();
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();

  const [lobbyMode, setLobbyMode] = useState<'SELECT' | 'HOSTING' | 'JOINED' | 'SEARCHING'>('SELECT');
  const [status, setStatus] = useState<'IDLE' | 'SEARCHING' | 'COUNTDOWN' | 'RACING' | 'FINISHED'>('IDLE');
  const [text, setText] = useState('');
  const [opponents, setOpponents] = useState<Opponent[]>([]);
  const [countdown, setCountdown] = useState(3);
  const [raceTimer, setRaceTimer] = useState(60);
  const [result, setResult] = useState<GameResult | null>(null);
  const [userRankPos, setUserRankPos] = useState(1);
  const [leaderboard, setLeaderboard] = useState<any[]>([]);

  const [loadingText, setLoadingText] = useState('Initializing Arena...');
  const [lobbyId, setLobbyId] = useState<string>('');
  const [hostName, setHostName] = useState<string>('');
  const [hostWpm, setHostWpm] = useState<number>(0);
  const [copied, setCopied] = useState(false);
  const [peerCount, setPeerCount] = useState(0);

  const raceIntervalRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const clockIntervalRef = useRef<ReturnType<typeof setInterval> | null>(null);
  const channelRef = useRef<any>(null);
  const currentStatsRef = useRef<GameResult | null>(null);

  const calculateAdjustedWpm = (wpm: number, accuracy: number) => wpm * (accuracy / 100);

  const getDifficultyByRank = (rank: Rank | undefined): Difficulty => {
    if (!rank) return Difficulty.EASY;
    const r = rank.toString();
    if (r.includes('Bronze') || r.includes('Silver')) return Difficulty.EASY;
    if (r.includes('Gold') || r.includes('Platinum')) return Difficulty.MEDIUM;
    return Difficulty.HARD;
  };

  useEffect(() => {
    if (!lobbyId || !user) return;
    const channel = supabase.channel(`lobby:${lobbyId}`, { config: { presence: { key: user.id } } });
    channel
      .on('presence', { event: 'sync' }, () => setPeerCount(Object.keys(channel.presenceState()).length))
      .on('broadcast', { event: 'START_RACE' }, (p) => {
        if (lobbyMode === 'JOINED') {
          setText(p.payload.text);
          setupOpponent(p.payload.hostData);
          setStatus('COUNTDOWN');
        }
      })
      .subscribe();
    channelRef.current = channel;
    return () => { channel.unsubscribe(); channelRef.current = null; };
  }, [lobbyId, user?.id, lobbyMode]);

  useEffect(() => {
    const lobby = searchParams.get('lobby');
    const host = searchParams.get('host');
    const mode = searchParams.get('mode');
    if (lobby) {
      setLobbyId(lobby);
      if (host) {
        setLobbyMode('JOINED'); 
        setHostName(decodeURIComponent(host));
        setHostWpm(parseInt(searchParams.get('wpm') || '40'));
      } else {
        setLobbyMode('HOSTING');
      }
    } else if (mode === 'host') {
      handleCreateLobby();
    }
    return () => {
      if (raceIntervalRef.current) clearInterval(raceIntervalRef.current);
      if (clockIntervalRef.current) clearInterval(clockIntervalRef.current);
    };
  }, [searchParams]);

  const handleCreateLobby = () => {
    setLobbyId(Math.random().toString(36).substring(7).toUpperCase());
    setLobbyMode('HOSTING');
  };

  const setupOpponent = (data: { name: string, wpm: number }) => {
    setOpponents([{ id: 'opp', name: data.name, progress: 0, wpm: data.wpm, accuracy: 96, color: COLORS[0], isFinished: false }]);
  };

  const startMatchmaking = async () => {
    setStatus('SEARCHING');
    setLoadingText("Fetching Terrain...");
    const textData = await generatePracticeText(getDifficultyByRank(user?.rank));
    setText((textData + " ").repeat(5));
    const bots: Opponent[] = [];
    for (let i = 0; i < 2; i++) {
        bots.push({
            id: `bot-${i}`,
            name: BOT_NAMES[Math.floor(Math.random() * BOT_NAMES.length)],
            progress: 0,
            wpm: Math.max(20, Math.floor(Math.random() * 30) + (user?.avgWpm || 40) - 10),
            accuracy: 95,
            color: COLORS[i],
            isFinished: false
        });
    }
    setOpponents(bots);
    setStatus('COUNTDOWN');
  };

  useEffect(() => {
    if (status === 'COUNTDOWN') {
      if (countdown > 0) {
        const t = setTimeout(() => setCountdown(c => c - 1), 1000);
        return () => clearTimeout(t);
      } else {
        setStatus('RACING');
        startBotRace();
        startRaceClock();
      }
    }
  }, [status, countdown]);

  const startRaceClock = () => {
    clockIntervalRef.current = setInterval(() => {
      setRaceTimer(prev => {
        if (prev <= 1) {
          clearInterval(clockIntervalRef.current!);
          handleFinish(currentStatsRef.current || { wpm: 0, accuracy: 100, errors: 0, timeTaken: 60, totalChars: 0, backspaces: 0, rawWpm: 0, wpmHistory: [], confusionMap: {}, characterStats: {} });
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  const startBotRace = () => {
    const start = Date.now();
    raceIntervalRef.current = setInterval(() => {
      setOpponents(prev => prev.map(bot => ({
        ...bot,
        progress: Math.min(100, (bot.wpm * 5 * ((Date.now() - start) / 60000) / text.length) * 100)
      })));
    }, 100);
  };

  const handleFinish = (res: GameResult) => {
    if (status === 'FINISHED') return; // Prevent double trigger

    if (raceIntervalRef.current) clearInterval(raceIntervalRef.current);
    if (clockIntervalRef.current) clearInterval(clockIntervalRef.current);

    const userScore = calculateAdjustedWpm(res.wpm, res.accuracy);
    const results = [
      { id: 'user', name: user?.username || 'You', wpm: res.wpm, accuracy: res.accuracy, score: userScore, isUser: true },
      ...opponents.map(b => ({ id: b.id, name: b.name, wpm: b.wpm, accuracy: b.accuracy, score: calculateAdjustedWpm(b.wpm, b.accuracy), isUser: false }))
    ].sort((a, b) => b.score - a.score).map((p, i) => ({ ...p, rank: i + 1 }));

    setLeaderboard(results);
    const rank = results.find(p => p.isUser)?.rank || results.length;
    setUserRankPos(rank);
    setResult(res);
    setStatus('FINISHED');

    addMatch({
      id: Date.now().toString(),
      date: new Date().toISOString(),
      wpm: res.wpm,
      accuracy: res.accuracy,
      mode: GameMode.MULTIPLAYER,
      difficulty: getDifficultyByRank(user?.rank),
      errors: res.errors
    }, rank === 1 ? 300 : rank === 2 ? 150 : 75, rank === 1);
  };

  if (status === 'IDLE' && lobbyMode === 'SELECT') {
    return (
      <div className="max-w-4xl mx-auto py-12 text-center">
        <h2 className="text-4xl font-black italic mb-12">ARENA SELECTION</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div onClick={startMatchmaking} className="p-12 bg-white/5 border border-white/10 rounded-3xl hover:border-neon-cyan cursor-pointer transition-all hover:-translate-y-2">
            <Globe size={48} className="mx-auto mb-4 text-neon-cyan" />
            <h3 className="text-2xl font-bold">RANKED MARATHON</h3>
            <p className="text-slate-500 mt-2">60s Global Endurance</p>
          </div>
          <div onClick={handleCreateLobby} className="p-12 bg-white/5 border border-white/10 rounded-3xl hover:border-neon-purple cursor-pointer transition-all hover:-translate-y-2">
            <Lock size={48} className="mx-auto mb-4 text-neon-purple" />
            <h3 className="text-2xl font-bold">PRIVATE DUEL</h3>
            <p className="text-slate-500 mt-2">1v1 Invitation Only</p>
          </div>
        </div>
      </div>
    );
  }

  if (status === 'SEARCHING') {
      return (
          <div className="flex flex-col items-center justify-center h-96">
              <Loader2 className="animate-spin text-neon-cyan mb-6" size={64} />
              <h2 className="text-2xl font-black uppercase tracking-widest">{loadingText}</h2>
          </div>
      );
  }

  return (
    <div className="max-w-5xl mx-auto">
      {status === 'FINISHED' && result ? (
        <ResultDashboard 
            result={result} 
            userRankPos={userRankPos} 
            leaderboard={leaderboard} 
            playerName={user?.username || 'Player'}
            onAction={(a) => a === 'REPLAY' ? window.location.reload() : navigate('/')}
        />
      ) : (
        <>
            <div className="mb-8 flex justify-between items-center border-b border-white/10 pb-6">
                <div className="flex items-center gap-3">
                    <Zap className="text-neon-cyan" size={24} />
                    <h2 className="text-2xl font-black uppercase italic tracking-tighter">Marathon Arena</h2>
                </div>
                <div className={`px-8 py-3 rounded-2xl border-2 transition-all ${raceTimer <= 10 ? 'border-neon-pink bg-neon-pink/10 animate-pulse' : 'border-neon-cyan bg-white/5'}`}>
                    <span className={`font-mono text-3xl font-black ${raceTimer <= 10 ? 'text-neon-pink' : 'text-white'}`}>
                        00:{raceTimer.toString().padStart(2, '0')}
                    </span>
                </div>
            </div>

            <TypingEngine 
                text={text}
                isGameActive={status === 'RACING'}
                onGameFinish={handleFinish}
                onStatsUpdate={(s) => { currentStatsRef.current = s; }}
                opponents={opponents}
                isTimedMode={true}
            />

            {status === 'COUNTDOWN' && (
                <div className="fixed inset-0 z-[110] flex items-center justify-center bg-black/60 backdrop-blur-md">
                    <div className="text-[15rem] font-black text-white italic animate-pop-in drop-shadow-[0_0_80px_rgba(6,182,212,0.8)]">
                        {countdown > 0 ? countdown : 'GO'}
                    </div>
                </div>
            )}
        </>
      )}
    </div>
  );
};

export default Multiplayer;

payload.text:
Fix WPM Calculation - Incorrect Speed Measurement**

The WPM (Words Per Minute) calculation is inaccurate. Implement correct WPM formula.

---

## **CORRECT WPM FORMULA:**

```javascript
// WRONG (likely current implementation)
const wpm = totalWords / timeInMinutes; // ❌

// CORRECT - Standard typing test formula
function calculateWPM(totalCharactersTyped, timeInSeconds, mistakes) {
  // Standard: 1 word = 5 characters (including spaces)
  const grossWPM = (totalCharactersTyped / 5) / (timeInSeconds / 60);

  // Adjust for errors (subtract mistakes)
  const netWPM = grossWPM - (mistakes / (timeInSeconds / 60));

  return Math.max(0, Math.round(netWPM)); // Never negative
}

// Real-time WPM (updates every second)
function calculateRealTimeWPM(charactersTypedSoFar, elapsedSeconds) {
  if (elapsedSeconds === 0) return 0;
  return Math.round((charactersTypedSoFar / 5) / (elapsedSeconds / 60));
}

// Example:
// Typed 250 characters in 60 seconds with 10 mistakes
// Gross WPM: (250 / 5) / 1 = 50
// Net WPM: 50 - (10 / 1) = 40 WPM ✅
```

---

**Ensure:**
- Count ALL typed characters (including spaces, punctuation)
- Time starts when first key pressed
- Mistakes are subtracted from gross WPM
- Updates in real-time during race

---

---

**PROMPT 2: Fix Text Generation - Repetitive Sentences & Paragraphs**

The practice text repeats the same paragraph every game and duplicates sentences within a single game. Generate unique, varied text for each session.

---

## **ISSUES:**
1. Same paragraph appears in every game
2. Same sentence repeats multiple times within one game
3. No variety in content

---

## **SOLUTION - Dynamic Text Generation:**

```javascript
// Use API for dynamic text generation
const TEXT_SOURCES = {
  // Option 1: Quotable API (random quotes)
  quotes: 'https://api.quotable.io/quotes/random?minLength=100&maxLength=300&limit=5',

  // Option 2: Bacon Ipsum (placeholder text)
  bacon: 'https://baconipsum.com/api/?type=all-meat&sentences=5',

  // Option 3: Random Text API
  randomText: 'https://random-word-api.herokuapp.com/word?number=50'
};

// Generate unique text for each game
async function generatePracticeText(difficulty) {
  const textPool = await fetchFromMultipleSources();

  // Shuffle and combine to avoid repetition
  const uniqueText = shuffleAndCombine(textPool);

  // Filter by difficulty
  const filteredText = filterByDifficulty(uniqueText, difficulty);

  return filteredText;
}

// Prevent sentence repetition
function ensureUniqueSentences(text) {
  const sentences = text.split('. ');
  const unique = [...new Set(sentences)]; // Remove duplicates
  return unique.join('. ');
}

// Store recently used texts to avoid repetition across games
const recentTexts = new Set();

async function getUniqueGameText() {
  let attempts = 0;
  let text;

  while (attempts < 5) {
    text = await generatePracticeText('medium');

    // Check if we've used this recently
    if (!recentTexts.has(text)) {
      recentTexts.add(text);

      // Keep only last 20 texts in memory
      if (recentTexts.size > 20) {
        const firstItem = recentTexts.values().next().value;
        recentTexts.delete(firstItem);
      }

      return ensureUniqueSentences(text);
    }

    attempts++;
  }

  // Fallback if all attempts exhausted
  return generateFallbackText();
}
```

---

## **ALTERNATIVE: Build Large Text Library:**

```javascript
// Create diverse text pool (stored locally)
const TEXT_LIBRARY = {
  easy: [
    "The quick brown fox jumps over the lazy dog near the old barn.",
    "She sells seashells by the seashore on sunny summer days.",
    "A journey of a thousand miles begins with a single step.",
    // ... 100+ unique sentences
  ],

  medium: [
    "Technology advances rapidly, transforming how we communicate and work in modern society.",
    "Ancient civilizations built remarkable structures that continue to amaze archaeologists today.",
    // ... 100+ unique sentences
  ],

  hard: [
    "Quantum mechanics revolutionized physics, introducing probabilistic interpretations of subatomic phenomena.",
    // ... 100+ unique sentences
  ]
};

// Randomly select and combine sentences
function generateFromLibrary(difficulty, sentenceCount = 5) {
  const pool = TEXT_LIBRARY[difficulty];
  const selected = [];
  const usedIndices = new Set();

  while (selected.length < sentenceCount) {
    const randomIndex = Math.floor(Math.random() * pool.length);

    if (!usedIndices.has(randomIndex)) {
      selected.push(pool[randomIndex]);
      usedIndices.add(randomIndex);
    }
  }

  return selected.join(' ');
}
```

---

## **IMPLEMENT:**
1. Use Quotable API or Random Text API for dynamic content
2. Never repeat same paragraph across games
3. Check for duplicate sentences within text
4. Store last 20 used texts to prevent repetition
5. Build fallback text library (100+ unique sentences)
6. Shuffle and randomize selection each time

---

**RESULT: Every game has completely unique, non-repetitive text!**

